css:
@-moz-document url-prefix(http://www.ign.com/boards/) {

	.listBlock{
	padding: 2px;
	}

	.Av96408823m,
	.Av96408823s,
	.Av4670901m,
	.Av4670901s,
	.Av5166750m,
	footer,
	.quick_reply_medrec,
	.noprint,
	.side_medrec,
	.sidebar,
	.spine,
	.ad_placement_300x250,
	#king.chess {
	display:none !important;
	}


	.mainContent{
	margin-right: 0px;
	}

	h3.title{
	font-size: 1.5em !important;
	}


	.navTab a{
	color: #fff !important;
	}

	.discussionListItem.visible.unread, .title, a{
	font-weight: 500 !important;
	line-height: 28px;
	}

	a:visited{
	font-weight: medium
	}

	.pageWidth{
	margin: 0 auto;
	max-width: 1200px;
	}

	blockquote span {
	color: black !important;
	}

	.threadLabels{
	background-color: white;
	}

	.Av1373280m, .Av1373280s{
	background-image: url("http://artecolote.com.s3.amazonaws.com/meme/hillters.png");
	background-size: contain !important;
	background-repeat: no-repeat ;
	}

	.Av1373280m img, .Av1373280s img{
	opacity:0;
	}

	a:link, a:visited{
	color: #444 !important;
	/* background-size: cover; */
	}

}
