css:
@-moz-document domain("fossmint.com") {
  site-content {
      padding: 5px 0 0;
  }

  .container {
      margin: 0 auto;
      max-width: 100%;
  }

  .vce-main-content {
      float: left;
      width: 84%;
      padding: 1%;
  }

  .sidebar {
      width: 15%;
      position: relative;
      min-height: 1px;
  }

  .vce-single .entry-content,
  .vce-single .entry-headline,
  .vce-single .entry-footer {
      width: 90%;
      text-align: justify;
      font-size: larger;
  }
}
