css:
@-moz-document url-prefix("http://vk.com"), url-prefix("https://vk.com") {
  #page_layout {
      width: 1100px !important;
  }
  #page_body {
      width: 935px !important;
  }
  input.text.ts_input {
      padding: 8px 6px 6px 10px;
      border: 0;
      box-sizing: border-box;
      margin: 2px 0;
      height: 38px;
      line-height: 16px;
      width: 500px;
      border-radius: 5px;
      transition: background-color 0.3s, color 0.3s;
      padding-left: 29px;
      border-top: 4px solid transparent;
      border-left: 15px solid transparent;
      background-position: left 8px;
      color: #d9e2ec;
  }
  #page_header {
    width: 1100px !important;
  }
  .top_home_link .top_home_logo {
    margin-left: 25px;
  }
  #page_header_cont .back {
      height: 52px;
  }
  #page_header_wrap {
      margin-top: 4px;
  }
  #ts_cont_wrap {
      width: 515px;
  }
  .page_block {
      margin: 20px 0 0;
  }
  .side_bar_inner {
      padding: 20px 0 10px;
  }
  .top_nav_btn {
      border-radius: 50px;
  }
  #side_bar .left_label {
    font-weight: bold;
  }
  #side_bar .left_label {
    font-weight: bold;
  }
  #stl_bg {
    padding-top: 70px;
  }
  #side_bar ol li .left_row {
    color: #444444;
  }
  .left_count_wrap {
    border-radius: 50px;
  }
  #side_bar ol li .left_row, 
  #side_bar ol li .left_row, 
  a.ts_contact, 
  .flat_button, 
  .ui_rmenu_item, 
  .ui_rmenu_subitem, 
  .top_nav_btn, 
  .top_nav_link.top_profile_link, 
  .profile_more_info_link, 
  .top_profile_mrow, 
  .groups_list_invites .ui_load_more_btn, 
  .top_audio_player.top_audio_player_enabled, 
  .page_actions_item,
  .ui_actions_menu_item,
  .nim-dialog {
    transition: background 0.2s;
  }
  .friends_field_act {
    padding: 0 0 0px;
  }
  .group_row_labeled > a, 
  .friends_field_title > a, 
  a.author, 
  .post_author .author  > a, 
  .wall_module .author  > a, 
  .wall_module  > a,
  a.copy_author,
  .topic_title > a, 
  .labeled > a, 
  .people_cell_name > a,
  .right_list_field > a, 
  a.friends_field_act, 
  a.app_link {
    position: relative;
    text-decoration: none;
  }
  .group_row_labeled > a:before, 
  .friends_field_title > a:before, 
  a.author:before, 
  .post_author .author  > a:before, 
  .wall_module .author  > a:before, 
  .wall_module  > a:before,
  a.copy_author:before,
  .topic_title > a:before,
  .labeled > a:before, 
  .people_cell_name > a:before, 
  .right_list_field > a:before, 
  a.friends_field_act:before, 
  a.app_link:before {
    content: "";
    position: absolute;
    width: 100%;
    height: 1px;
    bottom: 0;
    left: 0;
    background-color: #2a5885;
    visibility: hidden;
    -webkit-transform: scaleX(0);
    transform: scaleX(0);
    -webkit-transition: all 0.3s ease-in-out 0s;
    transition: all 0.3s ease-in-out 0s;
  }
  .group_row_labeled > a:hover:before, 
  .friends_field_title > a:hover:before, 
  a.author:hover:before,
  .post_author .author  > a:hover:before, 
  .wall_module .author  > a:hover:before, 
  .wall_module  > a:hover:before,
  a.copy_author:hover:before,
  .topic_title > a:hover:before, 
  .labeled > a:hover:before,
  .people_cell_name > a:hover:before, 
  .right_list_field > a:hover:before, 
  a.friends_field_act:hover:before, 
  a.app_link:hover:before {
    visibility: visible;
    -webkit-transform: scaleX(1);
    transform: scaleX(1);
  }
  .top_nav_link.top_profile_link {
    border-radius: 15px;
  }
  div.back {
    box-shadow: 0 4px 4px rgba(0, 0, 0, 0.2);
  }
  .group_row_buttons .flat_button, .friends_user_common .friends_controls .flat_button {
    border: 1px solid;
  	border-radius: 15px;
    transition: all 0.3s;
  }
  .group_row_buttons .flat_button:hover, .friends_user_common .friends_controls .flat_button:hover {
    color: #2c5784;
    background: #fff;
  }
  .flat_button.secondary.button_light:hover, .flat_button.secondary.button_light.button_small.friends_deny_request:hover {
    background-color: #5180b8;
    border: 1px solid #5180b8;
    color: #fff;
  }
  .page_photo .flat_button, .page_photo .page_actions_expanded, .page_photo .page_actions_wrap {
    border-radius: 15px;
  }
  .flat_button {
    	border-radius: 15px;
	}
  .box_controls .flat_button.secondary, .flat_button.secondary.button_light {
    border: 1px solid;
  }
  .profile_info .label {
    font-weight: bold;
  }
  .page_name, h2.page_name {
    font-weight: bold;
    color: #4975a8;
  }
  .is_online .profile_online_lv {
	text-transform: uppercase;
    font-weight: bold;
    color: #16cc00;
  }
  .current_text {
    font-size: 15px;
  }
  .my_current_info {
  	margin-top: 1px;
  }
  .audio_page_player_fixed_shown {
    padding-top: 10px;
  }
  .audio_page_player_fixed {
  	height: 65px;
  }
  .audio_page_layout .has_friends_block .audio_page__rows_wrap {
    width: 620px;
  }
  .audio_pl_item {
    margin-left: 25px
  }
  .apps_recent_block, .apps_recent_apps_wrap .apps_recent_all {
    width: 575px;
  }
  .apps_featured_slider {
    width: 935px !important;
  }
  .videocat_featured_big {
    width: 580px !important;
  }
  .videocat_featured_video {
    width: 300px;
  }
  .videocat_row {
    width: 935px;
  }
  .video_item {
    margin-left: 40px;
  }
  .videocat_featured_playlists {
    width: 910px;
  }
  .videocat_featured_playlist {
    width: 445px;
  }
  .video_items_list {
    width: 910px;
    margin-left: -10px;
  }
  .audio_page_layout .has_friends_block .audio_page__rows_wrap {
    width: 655px;
  }
  .apps_cat_wrap .apps_cat_row:nth-child(-n+5) {
    margin-left: 20px;
    margin-right: 20px;
  }
  .apps_collections_row {
    margin-right: 25px;
    margin-left: 25px;
    margin-top: 30px;
  }
  .page_square_photo {
    margin-left: 35px;
    border-radius: 5px;
  }
  .profile_info .label {
    padding-right: 100px;
  }
  .profile_more_info_link {
    padding-left: 265px;
  }
  #marketplace .market_content .market_row {
    margin: 10px 30px 10px 30px;
  }
  #profile_groups_link {
    padding: 7px 0 8px 250px;
  }
}
