css:
@-moz-document url-prefix("https://etud.insa-toulouse.fr/~annales/") {
body {
	background-color: #E9EAEB;
	font-family: Arial;
}

#global {
	width: 70%;
	background-color: #fff;
	padding: 0px;
	margin-top: 20px;
	box-shadow: 3px 2px 4px #888;
	border-radius: 2px;
}

#global p {
	margin: 10px;
	padding-bottom: 10px;
}


#global img {
	margin-left: 10px;
}

.dossier {
	text-decoration: none;
	background-color: #ededed;
	display:block;
    width: 100%;
	padding-left: 50;
	color: #000;
}

.dossier:hover, .ensemble_fichier:hover {
	text-decoration: none;
	background-color: #E42719;
	box-shadow: 3px 2px 4px #888;
	font-weight: bold;
	color: #fff;
}

.ensemble_fichier a:hover {
	color: #fff;
}

.ensemble_fichier{
	text-decoration: none;
	background-color: #ededed;
	display:block;
    width: 100%;
	color: #000;
}

.fichier, .zoombox {
	background-color: #4F4D50;
	box-shadow: 2px 1px 2px #000;
	padding: 0px 5px 3px 5px;
	color: #fff;
	text-decoration: none;
	border-radius: 2px;
}

#arborescence {
	background-color: #E42719;
	box-shadow: 3px 2px 4px #888;
	padding: 10px 0px 0px 0px;
	color: #fff;
	text-shadow: 1px 1px 2px #555;
	font-weight: bold;
	border-radius: 2px;
}

#arborescence a {
	background-color: #4F4D50;
	box-shadow: 2px 1px 2px #000;
	padding: 5px;
	color: #fff;
	text-decoration: none;
	border-radius: 2px;
}

#arborescence a:hover, .fichier:hover, .zoombox:hover {
	background-color: #E42719;
	box-shadow: 2px 1px 4px #000;
	text-decoration: none;
}
}
