css:
/* i really want this to be global */
html,
section.content {
    background: #222;
}
section.cover.has-mask .mask {
    background-color: #212121;
    opacity: 1;
}
#main {
    background: #333;
    color: #ccc;
}
.anchor span {
    color: #e6e6e6;
}
.markdown-section pre {
    background-color: #222;
}
.markdown-section pre>code {
    background-color: #222;
    color: #888;
}
.markdown-section p.tip,
.markdown-section tr:nth-child(2n) {
    background-color: #3a3a3a;
}
.markdown-section code {
    color: #6f380a;
    background-color: #c3c3c3;
}
pre:after {
    color: #676565;
}
aside.sidebar {
    background: #161616;
    color: #aaa;
}
.sidebar ul li a {
    color: #79899a;
}
.search>input {
    background: #111;
    color: #aaa;
}
.sidebar-toggle {
    background-color: hsla(0, 0%, 23%, 0.61);
}
.markdown-section td,
.markdown-section th {
    border-color: #696969
}
