css:
@-moz-document domain("git.sankuai.com") {
a.admin-link {
    display: none;
}
.aui-dropdown2-tailed.avatar-dropdown {
    position: initial;
    display: inline !important;
}
.aui-dropdown2.aui-style-default ul {
	display: flex;
}
.aui-list-truncate .avatar-with-name.participant-item {
    width: 34px;
	overflow: visible;
	color: white;
}
.paged-table tr.focused .aui-list-truncate .avatar-with-name.participant-item,
.paged-table tr:hover .aui-list-truncate .avatar-with-name.participant-item {
	color: #f2f2f2;
}
span.reviewers-label {
	display: block !important;
	margin-bottom: 1em;
	text-align: left !important;
	text-indent: 1.3em;
}
.pull-request-metadata .reviewers {
	margin-right: 30px;
}
.pull-request-metadata .reviewers .user-avatar {
    margin: 0 0 11px 12px;
}
.pull-requests-table>tbody>tr>.reviewers>a {
    display: none;
}
.aui-list-truncate .user-avatar {
 	margin-left: 0;   
}
.avatar-dropdown .aui-list-truncate {
    overflow: inherit;
}
.aui-dropdown2.aui-style-default ul {
    margin-right: 24px;
}
.aui-dropdown2.aui-style-default.aui-dropdown2-tailed.avatar-dropdown {
    border: 0;
}
.pull-requests-table>tbody>tr>.reviewers {
    padding-right: 20px;
}
.pull-request-metadata .reviewers .extras-dropdown-trigger,
.aui-dropdown2.aui-dropdown2-tailed:after {
	display: none;
}
.aui-dropdown2.aui-dropdown2-tailed:before {
    background-color: transparent;
}
}
