css:
@-moz-document url-prefix("https://exmo.com/ru/trade"), url-prefix("https://exmo.me/ru/trade") {
/* Chat */
body > div.container {
	max-width: 1156px;
	margin: 0 auto;
}
body.ng-scope:before {
	content: '';
	height: 80px;
	background: #272736;
	display: block;
	margin-bottom: -80px;
}
body.ng-scope:after {
	content: '';
	height: 408px;
	background: #272736;
	display: block;
	margin-top: -408px;
}
.section_inner {
	width: 100%;
}
/* Список валют */

body > div.container > div.row.trade_top {
	position: relative;
	bottom: 26px;
	background: none;
	height: 26px;
}
body > div.container > div.row.trade_top > div > div.top_pairs_wrap {
	padding: 0;
}
body > div.container > div.row.trade_top > div > div.top_pairs_wrap > ul > li > a {
	background: none;
}
body > div.container > div.row.trade_top > div > div.top_pairs_wrap > ul > li > a:hover {
	background: #fff;
	color: #000;
}
body > div.container > div.row.trade_top > div > div.top_pairs_wrap > ul > li > a.btn_active {
	background: #fff !important;
	color: #000 !important;
}
body > div.container > div.row.trade_top > div > div.top_pairs_wrap > ul > li:nth-child(1) > a,
body > div.container > div.row.trade_top > div > div.top_pairs_wrap > ul > li:nth-child(1) > a.btn_active {
	background: #347ffb!important;
	color: #fff !important;
}
body > div.container > div.row.trade_top.ng-scope > div > div.top_pairs_settings {
	position: relative;
	padding: 0;
	bottom: 30px;
	left: 37px;
}
body > div.container > header > div > div.user_block > div.user_block_name {
	border-radius: 0;
}
#currency_pairs_settings {
	background: #2e2e3f;
	border-radius: 0 50px 50px 0;
	padding: 8px 2px 8px 30px;
}
#currency_pairs_settings:before {
	top: 7px;
}
body > div.container > header > div > div.user_default > a.btn_header.btn_registration {
	border-radius: 50px 0  0 50px;
}
/* Индексы */

body > div.container > div.row.graph_indexes {
	position: relative;
	bottom: 20px;
}
/* График */

body > div.container > div.row.chart_chat {
	position: relative;
	bottom: 24px;
}
/* Трейд */

body > div.container > div:nth-child(5) {
	position: relative;
	bottom: 44px;
	z-index: 9999;
}
body > div.container > div:nth-child(5) > div > div.row.market_order {
	width: 49%;
	float: left;
}
body > div.container > div:nth-child(5) > div > div.row.orders {
	width: 49%;
	float: right;
}
body > div.container > div:nth-child(5) > div > div.row.market_order.ng-scope > div.col_1,
body > div.container > div:nth-child(5) > div > div.row.market_order.ng-scope > div.col_2,
body > div.container > div:nth-child(5) > div > div.row.orders > div.col_1,
body > div.container > div:nth-child(5) > div > div.row.orders > div.col_2 {
	width: 100%;
}
body > div.container > div:nth-child(5) > div > div.row.trading_history.ng-scope {
	float: right;
}
}
