css:
@-moz-document domain('enewspaper.latimes.com') {
[id^=ext-articleview-] {
    width: 100% !important;
    height: 100% !important;
    min-width: 100% !important;
    min-height: 100% !important;
}

[id^=ext-articleview-] .contentBody {
    max-width: 70%;
    color: #002b36 !important;
    line-height: 1.3em;
    margin-left: 15%;
    font-size: 1.3em;
}

[id^=ext-articleview-] .x-container {
    background-color: #fdf6e3 !important;
}
}
