css:
@-moz-document domain("www.gdax.com") {
[class*='ProductSelection_choose-product_']:not([class*='ProductSelection_showing']) [class*='ProductSelection_choose-product-list'] {
    opacity: 1;
    top: inherit;
    bottom: 0;
    position: fixed;
    left: 0;
    background: #2A353D;
    color: #fff;
    box-shadow: none;
    border-radius: 0;
    z-index: 100;
}

[class*='ProductSelection_choose-product_']:not([class*='ProductSelection_showing']) [class*='ProductSelection_choose-product-list'] * {
    color: white;
}

[class*='ProductSelection_choose-product-list']:before, [class*='ProductSelection_choose-product-list']:after {
    display: none;
}

[class*='ProductSelection_choose-product_']:not([class*='ProductSelection_showing']) [class*="ProductSelectionItem_price_"] {
    font-weight: bold;
}

[class*='ProductSelection_choose-product_']:not([class*='ProductSelection_showing']) [class*="ProductSelection_products_"] {
    border: none;
}

[class*='ProductSelection_choose-product_']:not([class*='ProductSelection_showing']) [class*="ProductSelectionItem_product_"] a {
    padding-left: 0;
}

}
