css:
@-moz-document url-prefix("https://golang.org/doc/") {
body {
    background-color: #1f1f1f !important;
    color: white;
}
td {
    background-color: transparent !important;
    color: white !important;
}
pre {
    background-color: #454545 !important;
    color: #27FF00;
}
div#topbar.wide {
    background-color: transparent;
    color: white;
}
div#footer {
    background-color: transparent;
    color: white;
    font-style:italic;
    font-size:110%;
}
a:link,
a {
    background-color: black;
    color: white !important;
    font-weight: bold;
    text-decoration: underline green;
    font-size: 110%;
}
h3 {
    background-color: transparent;
    color: #1BFF00;
    font-size: 150%;
}
h1,
h2 {
    background-color: black;
    color: #1BFF00;
    font-size: 200%;
    font-weight: bold;
}
}
