css:
a
{
    background-position: 0 15%;
    background-size: 100% 200%;
background-image: linear-gradient(to bottom,transparent 50%,rgba(217,21,54,.75) 50%);
    transition: 200ms;
-webkit-transition: 200ms;
-moz-transition: 200ms;
-o-transition: 200ms;
}
a:hover
{
    background-position: 0 95%;
color: #ffffff;
}
