css:
/* style pompé à 99% sur le style Ave2sucres de Victime et le style Fixes en tout genres de Chef*/
/* HEADER + NAVBAR */
/* Header Sombre */
@-moz-document url-prefix("https://2sucres.org") {

/* Header + Navbar */
  .ms-navbar
  {
    background-color: #080808;
  }

  .ms-header.ms-header-primary
  {
    background-color: #212121;
  }

  .ms-header .ms-title img,body > div.sb-site-container > nav > div > div.navbar-header > a
  {
    content: url("https://image.noelshack.com/fichiers/2017/51/1/1513618811-luma2sucres-logo-gros.png");
  }

 
/* Police d'écriture Navbar */

#navbar > ul > li > a {
    font-weight: normal;
    text-transform: uppercase;
    letter-spacing: 1.5px;
}
/*	BODY */
/* Background */

body .sb-site-container {
    background-color: #24221a;
    background-image: url(https://image.noelshack.com/fichiers/2017/51/1/1513616721-luma-fond.png);
    background-size: contain;
    background-repeat: repeat;
}


/*Boutons oranges en bleu*/
.md-button.md-default-theme.md-hue-1.md-accent.md-raised, .md-button.md-hue-1.md-accent.md-raised, .md-button.md-default-theme.md-hue-1.md-accent.md-fab, .md-button.md-hue-1.md-accent.md-fab, .md-button.md-default-theme.md-hue-1.md-fab, .md-button.md-hue-1.md-fab, .md-button.md-default-theme.md-accent.md-raised, .md-button.md-accent.md-raised, .md-button.md-default-theme.md-accent.md-fab, .md-button.md-accent.md-fab, md-switch.md-default-theme.md-hue-1.md-checked .md-thumb, md-switch.md-hue-1.md-checked .md-thumb,md-switch.md-default-theme.md-checked .md-thumb, md-switch.md-checked .md-thumb,.sucre-pagi > li.active > a {
    background-color:#88f3e6;
    color:#fff;
}
.md-button.md-default-theme.md-hue-1.md-accent.md-raised:not([disabled]):hover, .md-button.md-hue-1.md-accent.md-raised:not([disabled]):hover, .md-button.md-default-theme.md-hue-1.md-accent.md-fab:not([disabled]):hover, .md- button.md-hue-1.md-accent.md-fab:not([disabled]):hover, .md-button.md-default-theme.md-hue-1.md-fab:not([disabled]):hover, .md-button.md-hue-1.md-fab:not([disabled]):hover, .md-button.md-default-theme.md-accent.md-raised:not([disabled]):hover, .md-button.md-accent.md-raised:not([disabled]):hover, .md-button.md-default-theme.md-accent.md-fab:not([disabled]):hover, .md-button.md-accent.md-fab:not([disabled]):hover {
    background-color:#64beb3;
    color:#fff;
}

md-switch.md-default-theme.md-hue-1.md-checked .md-bar, md-switch.md-hue-1.md-checked .md-bar, md-switch.md-default-theme.md-checked .md-bar, md-switch.md-checked .md-bar {
    background-color:#64beb3;
}

/* FOOTER */
/* Background */

.ms-footer {
    border-top: 4px solid #005a69;
    background-color: #080808;
}
}
