css:
@-moz-document domain(www.cawdvt.org) {
body {
	background-color: #37474F;
    color: white;
}
* {
    color: inherit;
}
a {
    color: #009688;
}
a:visited {
    color: #80CBC4;
}
a:hover {
    color: #4DB6AC !important;
}
nav {
    box-shadow: 3px 3px 5px rgba(0,0,0,0.1);
	background-color: #455A64;
}
nav * {
	color: inherit !important;
}
img#cawdAdaptLogo {
    box-shadow: 5px 5px 11px rgba(0,0,0,0.3);
    height: 50px !important;
    width: 50px !important;
	background-color: white;
    padding: 0.5rem;
    border-radius: 50%;

   	/* filter: drop-shadow(0px 0px 10px 10px red); */

}

footer#colophon {
	background-color: #263238;
}

/* Red override */
span[style="color: #ff0000;"] {
	color: #E91E63 !important;
}
.bg-secondary {
    background-image: url('https://i.imgur.com/voAJ0PU.jpg');
    background-position: center;
    background-size: 400px 400px;
    background-attachment: fixed;
}
/*   Stephen Gheysens Made this gaming texture: https://i.imgur.com/T0pgHjm.png */
}
