css:
@-moz-document domain("animenewsnetwork.com") {
body {
    filter: invert(1) hue-rotate(180deg);
    background-color: #181818;
}

nav#mega {
    filter: invert(1) hue-rotate(180deg);
}

.thumbnail, #marquee, iframe, img {
    filter: invert(1) hue-rotate(180deg);
}

#page {
    width: auto !important;
}

.logo img {
    filter: none;
}

.iab {
    display: none !important;
}

#mainfeed #aside {
    background: #e7e7e7;
}
}
