css:
@-moz-document domain("cuvantul-ortodox.ro") {
body{
    background:#555;
    width:99%;
}

#content{
    background:#e5e5e5;
    color:#111;
}

.container {
    box-shadow:none;
}

.span4 .widget{
    border:0;
    background:none;
    padding:0;
}

ul.recentposts-widget li{
	background:#eee;
    border:0!important;
    box-shadow: 2px 2px 2px -2px #111;
}

span[style*="color: #ff0000;"]{
	color: #ce0b0b!important;
}

.menu-topmenu-container .menu-item {
    border-radius: 0!important;
    border:0!important;
    margin:1px;
    padding:0;
}

.menu-topmenu-container .menu-item a {
    display:block;
    padding:6px;
    -webkit-transition: none;
}

.searchform{
    margin: 2px -7px 0 3px;
}

.s {
    border-radius:0!important;
}
}
