css:
@-moz-document url-prefix(https://alt.org/nethack/dudley/) {
    .dudley {
        background-color: #2F4F4F !important;
    }
    .comic, .panelcontainer, .txt, .footertxt, textarea {
        background-color: #6D8383 !important;
    }
    span, h1, h2, h3, div, p, a {
        color: white;
    }
    th, .txt > p {
        color: black;
    }
    th > a {
        color: black;
        text-decoration: none;
    }
    .comic, .txt {
        padding: 0px !important;
        margin: 0px !important;
        border: 0px !important;
    }
}
