css:
@-moz-document domain("www.worldstarhiphop.com") {
.box .title a {
    color: #fefefe;
}
#main {
    background: #121212;
}
.carousel .title {
    color: #fff;
}
body {
    background: #121212;
}
.container {
    border: 1px solid #ef0303;
    background: #121212;
}
.heading {
    background: linear-gradient(to bottom, #991a1a 0%, #000 36%, #000000 90%, #962d2d 100%);
}
.heading h1 {
    color: #fefefe;
}
.views,
.comments,
.views a,
.comments a {
    color: #fff;
}
body {
    color: #ffffff;
}
#footer .f3 {
    background: linear-gradient(to bottom, #991a1a 0%, #752929 100%);
}
.footer-nav a {
    color: #ffffff;
}
.privacy-policy a {
    color: #ff0000;
}

.video-list .title a {
    color: #ffffff;
}

.information .date {
    color: #fff;
}

.watch-view-count {
    color: #dc3636;
}

.comments-holder {
    background: #000000;
}

.two-columns:before {
    background: linear-gradient(to bottom, #000 0%,#000000 100%);
}
}
