css:
/* i really want this to be global */

/* main */

    body, div.container, div.navbar-inner {
        background-color: #2f343f !important;
        background-image: none !important
    }

    button#post-note-button.btn.btn-small.primary, button#post-picture-button.btn.btn-small.primary, .btn-success, span.badge.badge-important {
        background-color: #707596 !important;
        background-image: none !important;
        color: #c2c2c2 !important;
        text-shadow: none !important;
        border: none !important
    }

    img.img-rounded.media-object {
        border-radius: 50px !important;
        border: 3px solid #393f4f !important;
	width: 84px !important;
	height: 84px !important
    }

    .media-heading {
        color: #c2c2c2 !important;	
    }

    ul.dropdown-menu {
        background-color: #2f343f !important;
    }

    li.divider {
        display: none !important
    }

    a, h3, p, b {
        color: #c2c2c2 !important
    }

    a:-webkit-any-link {
        color: #ffffff !important
    }

    a#profile-dropdown.dropdown-toggle {
        background-color: #2f343f !important;
    }

    .replies .media {
	background-color: #2b2c31 !important;
	border-left: 10px solid #30343f !important;
    }

/* post editor */

    div.modal-header, div.modal-body, div.modal-footer, iframe.wysihtml5-sandbox, li.select2-search-field, ul.select2-choices {
        background-color: #2f343f !important;
        color: #c2c2c2 !important;
        background-image: none !important;
        box-shadow: none !important
    }

    body, div.container, div.navbar-inner {
        color: #c2c2c2 !important
    }

    a.btn, .btn-primary {
        background-color: #707596 !important;
        background-image: none !important;
        color: #c2c2c2 !important;
        text-shadow: none !important;
        border: none !important
    }
