css:
@-moz-document domain(drupal.org) {
  @media (min-width: 65.625em) {
  .node-type-guide #site-name a,
  .node-type-documentation #site-name a {
    font-size: 2.5em;
  }
}
@media (min-width: 47.5em) {
  .node-type-guide #page .breadcrumb,
  .node-type-documentation #page .breadcrumb {
    font-size: inherit;
  }
  .node-type-guide #page h1
  .node-type-documentation #page h1 {
    padding-right: 0px;
  }
}
.node-type-guide #page .breadcrumb,
.node-type-documentation #page .breadcrumb {
  font-size: inherit;
}
.node-type-guide h1,
.node-type-documentation h1,
.node-type-page h1,
.node-type-section h1,
.node-type-post h1,
.node-type-casestudy h1,
.page-download h1 {
  font-size: 2em;
}
.pane-node-body {
  font-size: 90%;
  line-height: 1.2em;
}
code, tt, pre {
  font-size: 90%;
}
} /* End of non-global block */
