css:
@-moz-document domain(myshows.me){
/*** Список загрузки слева от кнопки ***/
.buttonPopup ul{
left:initial !important;
right: 100%;
}


/*** Полоска разделитель меду фильмами сверху ***/
.showHeader{
/*background:red;*/
border-top: 1px dotted #ccc  !important;;
border-bottom: none  !important;;
}

/*** Блок отображения названий ***/
.showHeaderName{
float: initial;
font-size: inherit !important;
}

.showHeaderName > a{
float: left;
}

.showHeaderName .subHeader{
float: right; 
font-size: inherit !important;
}

/*** Убираем OnAir и продолжительность в часах ***/
.showHeaderName > sup {display: none !important;}
.showHeaderMeta {  display: none !important; }


/*** Убираем подчёркивание сезона ***/
.seasonBlockHeader{
font-size: 1.4rem;
border-bottom: none  !important;
}

.seasonBlockBody{
border-bottom: none  !important;
}

/***  ***/
main.col8._borderLeft > h3{
border-top: 1px dotted #ccc !important;
}

.firmTable._withBorder{
border: none  !important;
}

ins, .contAlt, ._wide {  display: none !important; }
}
