css:
@-moz-document domain("manhuagui.com")
{
	.main-btn,
	.footer,
	.gg_980,
	.img_info {
		display: none !important;
	}

	body {
		font: 11pt sans-serif;
		padding: 4mm 0 0;
		min-height: calc(100vh - 12mm);
		background: linear-gradient(90deg,
				rgba(128, 128, 128, .5) 0,
				rgba(128, 128, 128, .3) 20%,
				rgba(128, 128, 128, .3) 60%,
				rgba(128, 128, 128, .5) 100%);
	}

	body>.header {
		position: fixed;
		top: -6mm;
		left: 0;
		width: 100vw;
		margin: 0;
		z-index: 11;
		transition: top .8s .5s;
	}

	body>.header:hover {
		top: 0;
		transition: top .2s 0s;
	}

	.nav-sec li {
		width: 54px;
	}

	.update {
		height: auto;
	}

	.update-cont {
		height: 21vw;
		min-height: 230px;
	}

	.update-wrap {
		width: 500vw;
		min-width: 5000vw;
	}

	.update-cont ul {
		width: calc(100vw - 24mm);
		min-width: 1000px;
	}

	.update-cont li {
		width: calc(100% / 6 - 26px);
	}

	.update-cont .bcover,
	.update-cont .bcover .bg,
	.update-cont .bcover img {
		width: 100%;
		height: 100%;
		min-height: 200px;
	}

	.bcover img {
		object-fit: cover;
	}

	.w980.title {
		position: fixed;
		left: 2vw;
		bottom: 5mm;
		width: 96vw;
		line-height: 2em;
		border: none;
		padding: 0;
		z-index: 2;
	}

	.w980.sub-btn {
		margin: 0 auto;
		border: none;
		opacity: .3;
		transition: .3s;
	}

	.w980.sub-btn:hover {
		opacity: 1;
	}

	#page,
	.img_page,
	.title h1,
	.title h1 a,
	.title h2 {
		font: 400 12pt sans-serif;
		color: #b66;
	}

	.tbCenter {
		width: 100%;
		height: auto;
		max-width: 100vh;
		margin: 0 auto;
		border: none;
	}

	.w998 {
		width: auto;
		min-width: 1000px;
		max-width: 2100px;
		padding: 0 12mm;
	}

	.crumb {
		margin-bottom: 4mm;
	}

	.w728,
	.fl[style="width: 728px;"] {
		width: calc(100% - 270px) !important;
	}

	.chapter-cont {
		width: calc(100% - 300px);
	}

	.chapter,
	.chapter-list,
	.chapter-cont h5 {
		width: 100%;
	}

	.book-detail {
		width: calc(100% - 200px);
	}

	.tbCenter,
	.img-loading {
		background-color: transparent;
	}

	.img-loading {
		background-color: #fff;
		border-radius: 3mm;
	}

	#mangaBox {
		margin-top: 3mm;
	}

	#mangaBox,
	#mangaMoreBox {
		direction: rtl;
	}

	#mangaBox img,
	#mangaMoreBox img {
		display: inline-block !important;
		object-fit: contain;
		height: calc(100vh - 20mm);
		width: auto;
		margin: 0 0 2mm;
		max-width: 100%;
		border-radius: 2mm;
	}

	#pagination {
		position: fixed;
		bottom: -7mm;
		left: 0;
		width: 100vw;
		background: #888;
		padding: 2mm 0;
		z-index: 11;
		transition: bottom .8s .5s;
	}

	#pagination:hover {
		bottom: 0;
		transition: bottom .2s 0s;
	}

	#pagination a,
	#pagination span {
		min-width: 1em;
		border-radius: 2mm;
		margin: 0 1mm;
	}

	.nav-sub .nav-sub-cont-bg {
		display: block;
		opacity: 0;
		visibility: hidden;
		transition: .7s .3s;
	}

	.nav-sub-over .nav-sub-cont-bg {
		opacity: .93;
		visibility: visible;
		transition: .1s 0s;
	}

	.more div.content {
		display: block;
		visibility: hidden;
		opacity: 0;
		transition: .3s;
	}

	.more-hover div.content {
		visibility: visible;
		opacity: 1;
		transition: .1s;
	}

	.latest-list,
	.latest-list ul,
	.idx-mc-cont ul {
		width: auto;
	}

	.cover-list,
	.cmt-cont,
	.idx-mc-cont {
		width: auto;
		height: auto;
		padding: 2mm;
	}

	.cover-list li,
	.idx-mc-cont li,
	.latest-list li {
		display: inline-block;
		float: none;
		width: calc(100% / 5 - 4mm);
		height: auto;
		padding: 0 2mm;
		margin: 0 0 2mm;
	}

	.similar-list li {
		width: calc(100% / 6);
		padding: 0;
	}

	.cover {
		height: 22vw;
		box-shadow: 0 0 1mm #777;
		border-radius: 2mm;
		overflow: hidden;
	}

	.latest-list li:hover .cover {
		background: rgba(255, 255, 255, .3);
	}

	.cover,
	.cover .bg,
	.latest-list p,
	.cover .mk,
	.cover .tt {
		width: 100%;
		text-align: center;
	}

	.cover .bg {
		height: 100%;
	}

	.scover .bg, .cover .bg, .bcover .bg, .hcover .bg {
		background: linear-gradient(0deg,
				rgba(0, 0, 0, .6) 0,
				rgba(0, 0, 0, .6) 1em,
				rgba(0, 0, 0, 0) 2.5em);
	}

	.scover .mk, .cover .mk, .bcover .mk, .hcover .mk, .scover .tt, .cover .tt, .bcover .tt, .hcover .tt {
		bottom: .2em;
		margin: 0 0 0 -3mm;
		text-align: right;
	}

	.cover img {
		width: 100%;
		height: 100%;
		object-fit: cover;
	}

	.idx-sc-cont {
		width: calc(25% - 1px) !important;
	}

	.rank-cont li h6 {
		width: calc(100% - 6em);
	}

	@media screen and (min-width:1200px) {

		.cover-list li,
		.idx-mc-cont li,
		.latest-list li {
			width: calc(100% / 6 - 4mm);
		}

		.cover {
			height: 18vw;
		}
	}

	@media screen and (min-width:1600px) {

		.cover-list li,
		.idx-mc-cont li,
		.latest-list li {
			width: calc(100% / 8 - 4mm);
		}

		.cover {
			height: 16vw;
		}
	}

	@media screen and (min-width:2000px) {

		.cover-list li,
		.idx-mc-cont li,
		.latest-list li {
			width: calc(100% / 10 - 4mm);
		}

		.cover {
			height: 12vw;
		}
	}

	@media screen and (min-width: 136vh) {
		.tbCenter {
			max-width: 136vh;
		}

		#mangaMoreBox img {
			border-radius: 0;
		}
	}
}
