css:
@-moz-document domain("manhuagui.com") {
html {
	background:#ccc
}
.tbCenter {
	border:none;
	background:none;
	width:auto
}
#mangaBox {
	width:100%
}
img[data-tag="mangaFile"] {
	cursor:pointer;
	object-fit:contain;
	max-width:calc(100vw - 2mm);
	height:94.5vh;
	border-radius:2mm;
	box-shadow:0 1mm 5mm gray;
	margin-bottom: .3mm;
}
body>.header,
.main-btn {
	opacity:0.2;
	z-index:100
}
body>.header {
	background:#444;
	position:fixed;
	width:100vw;
	top:-30px;
	left:0;
	transition:all .8s
}
body>.header:hover {
	opacity:0.9;
	top:0;
	transition:all .2s
}
.main-btn {
	position:fixed;
	width:100vw;
	bottom:-30px;
	left:0;
	background:#444;
	padding:5px;
	transition:all .8s
}
.main-btn:hover {
	opacity:0.9;
	bottom:0;
	transition:all .2s
}
.title {
	margin-top:.5em;
	border:none;
	line-height:1.6em;
	text-align:center
}
.title h1,
.title h2,
#page,
.img_page {
	font-size:11pt
}
.pager,
.footer,
.gg_300,
.gg_950,
.gg_728,
.sub-btn {
	display:none !important
}
}
