css:
@-moz-document domain("manhuagui.com")
{
.main-btn,
.footer,
.gg_980,
.img_info {
	display: none !important;
}

body {
	font: 11pt sans-serif;
	padding-top: 4mm;
	min-height: calc(100vh - 12mm);
	background: linear-gradient(90deg,
			rgba(128, 128, 128, .5) 0,
			rgba(128, 128, 128, .3) 20%,
			rgba(128, 128, 128, .2) 60%,
			rgba(128, 128, 128, .5) 100%);
}

body>.header {
	position: fixed;
	top: -6mm;
	left: 0;
	width: 100vw;
	z-index: 11;
	border: none;
	transition: top .6s .3s;
}

body>.header:hover {
	top: 0;
	transition: top .2s 0s;
}

.nav-sec li {
	width: 3.2em;
}

.topper {
	position: relative;
	top: -4mm;
}

.user-area,
.user-feedback {
	width: auto;
	min-width: 4em;
}

.update,
.cmt-list {
	height: auto;
}

.update-cont {
	height: 21vw;
	min-height: 230px;
	max-height: 310px;
}

.update-wrap {
	width: auto;
	min-width: 5000%;
}

.update-cont ul {
	width: calc(100vw - 24mm);
	min-width: 1000px;
}

.update-cont li {
	width: calc(100% / 6 - 26px);
	margin: 0 12px;
}

.w980.title {
	position: fixed;
	left: 1vw;
	bottom: 5mm;
	width: 98vw;
	line-height: 2em;
	border: none;
	padding: 0;
	z-index: 2;
}

.w980.title * {
	color: #fff;
}

.w980.title>div {
	display: inline-block;
	padding: 1mm 2mm;
	font: 11pt sans-serif;
	vertical-align: middle;
	line-height: 1;
	border-radius: 2mm;
	text-shadow: 0 0 3px #000;
	background: rgba(0, 0, 0, .4);
	box-shadow: 0 0 3px 3px rgba(0, 0, 0, .4);
	opacity: .4;
	transition: .2s;
}

.w980.title>div:hover {
	opacity: 1;
}

.w980.sub-btn {
	width: auto;
	background: none;
	margin: 0 auto;
	border: none;
	opacity: .2;
	z-index: 2;
	transition: .3s;
}

.w980.sub-btn:hover {
	opacity: 1;
}

.w980.sub-btn>div {
	background-color: #ccc;
}

.support li a {
	background-color: transparent;
}

#page,
.img_page,
.title h1,
.title h1 a,
.title h2 {
	font: 400 12pt sans-serif;
}

.tbCenter {
	width: 100%;
	height: auto;
	max-width: 100vh;
	margin: 0 auto;
	border: none;
	background: none;
}

.w998 {
	width: auto;
	min-width: 1000px;
	max-width: 2100px;
	margin: 0 12mm;
	box-shadow: none;
}

.crumb {
	margin-bottom: 4mm;
}

.w728,
.fl[style="width: 728px;"] {
	width: calc(100% - 270px) !important;
}

/* ============================================= */
.box-gray {
	border: none;
}

.filter-nav {
	max-height: 3em;
	padding: 2mm;
	background: #eee;
	overflow: hidden;
	border-radius: 0 0 3mm 3mm;
	box-shadow: inset 0 0 2mm #555;
	transition: .5s;
}

.filter-nav:hover {
	max-height: 25em;
	transition: .2s;
}

.filter-nav .filter {
	width: auto;
	height: auto;
	vertical-align: top;
	border-bottom-color: #ccc;
}

.filter-nav .filter label {
	display: inline-block;
	float: none;
	width: 6em;
	line-height: 1.5;
	text-align: right;
	font: 600 11pt sans-serif;
	padding: 0;
}

.filter-nav .filter ul {
	display: inline-block;
	float: none;
	width: auto;
	line-height: 1.5 !important;
	max-width: calc(100% - 6em);
	font: 400 11pt sans-serif;
}

.filter-nav .filter li {
	display: inline-block;
	float: none;
	padding: 0 .2em;
}

.book-list ul {
	width: auto;
	margin: 0;
}

.book-similar {
	height: auto;
}

.book-list li {
	display: inline-block;
	float: none;
	width: calc(100% / 6 - 4mm - 1px);
	height: auto;
	margin: 2mm;
}

.similar-list li {
	display: inline-block;
	float: none;
	width: calc(100% / 6 - 6mm);
	height: auto;
}

.similar-list p {
	width: auto;
	margin: 0;
}

.scover .bg, .cover .bg, .bcover .bg, .hcover .bg,
.bcover,
.bcover .bg,
.bcover img,
.scover,
.scover .bg,
.scover img {
	width: 100%;
	height: 100%;
	min-height: 200px;
	max-height: 280px;
	border-radius: 2mm;
}

.cover img,
.bcover img,
.hcover img,
.scover img {
	object-fit: cover;
	padding: 0;
}

.cover .sl,
.bcover .sl,
.hcover .sl,
.scover .sl {
	top: -2px;
	left: -2px;
}

.chapter,
.chapter-list,
.chapter-cont h5 {
	width: 100%;
}
/* ====================================== */
.reply_content {
	padding: 1mm 5mm 0;
	margin: 1mm 0;
	border-radius: 2mm;
	box-shadow: inset 0 0 2mm #777;
}
.reply_content .comment_con_li {
	margin: 0;
	padding: 2mm 0;
}
.chapter-cont {
	width: calc(100% - 300px);
}

.book-detail {
	width: calc(100% - 200px);
}

.img-loading {
	background-color: #fff;
	border-radius: 3mm;
}

#mangaBox {
	margin-top: 2mm;
}

#mangaBox,
#mangaMoreBox {
	direction: rtl;
}

#mangaBox img,
#mangaMoreBox img {
	display: inline-block !important;
	object-fit: contain;
	height: calc(100vh - 20mm);
	width: auto;
	margin: 0 0 9px;
	max-width: 100%;
	border-radius: 2mm;
}

#pagination {
	position: fixed;
	bottom: -7mm;
	left: 0;
	width: 100vw;
	background: #888;
	padding: 2mm 0;
	z-index: 11;
	transition: bottom .8s .5s;
}

#pagination:hover {
	bottom: 0;
	transition: bottom .2s 0s;
}

#pagination a,
#pagination span {
	min-width: 1em;
	border-radius: 2mm;
	margin: 0 1mm;
}

.nav-sub .nav-sub-cont-bg {
	display: block;
	opacity: 0;
	visibility: hidden;
	transition: .7s .3s;
}

.nav-sub-over .nav-sub-cont-bg {
	opacity: .93;
	visibility: visible;
	transition: .1s 0s;
}

.more div.content {
	display: block;
	visibility: hidden;
	opacity: 0;
	transition: .3s;
}

.more-hover div.content {
	visibility: visible;
	opacity: 1;
	transition: .1s;
}

.latest-list,
.latest-list ul,
.idx-mc-cont ul {
	width: auto;
}

.cover-list,
.cmt-cont,
.index-cont,
.idx-mc-cont,
.idx-rank-cont {
	width: auto;
	height: auto;
	padding: 2mm;
	vertical-align: top;
}

.cmt-cont,
.index-cont,
.idx-mc-cont,
.idx-rank-cont,
.book-similar,
.comment,
.reply_content,
.update {
	background: rgba(255, 255, 255, .5);
	border: none;
}

.cover-list li,
.idx-mc-cont li,
.latest-list li {
	display: inline-block;
	float: none;
	width: calc(100% / 5 - 4mm);
	height: auto;
	padding: 0 2mm;
	margin: 0 0 2mm;
}

.index-cont>h3,
.index-cont>ul {
	float: none;
	display: inline-block;
}

.cover {
	height: 22vw;
	max-height: 260px;
	border-radius: 2mm;
}

.cover:hover {
	box-shadow: 0 0 2mm #40a;
}

.scover img,
.cover img,
.bcover img,
.hcover img {
	opacity: 1;
}

.cover,
.cover .bg,
.latest-list p,
.cover .mk,
.cover .tt {
	width: 100%;
	text-align: center;
}

.cover img {
	width: 100%;
	object-fit: cover;
}

.cover img,
.cover .bg {
	height: 100%;
	border-radius: 2mm;
}

.scover .bg,
.cover .bg,
.bcover .bg,
.hcover .bg {
	background: linear-gradient(0deg,
			rgba(0, 0, 0, .7) 0,
			transparent 3em);
}

.scover .mk,
.cover .mk,
.bcover .mk,
.hcover .mk,
.scover .tt,
.cover .tt,
.bcover .tt,
.hcover .tt {
	width: 90%;
}

.idx-sc-cont {
	width: calc(25% - 1px) !important;
}

.rank-cont li h6 {
	width: calc(100% - 6em);
}

.smh-msg-box {
	top: 30vh !important;
	left: 15vw !important;
	margin: 0 !important;
	width: 70vw;
	height: 40vh;
}

.smh-msg-cont {
	background: rgba(60, 60, 60, .8);
	box-shadow: 0 0 1mm #fff;
}

.smh-msg-cont,
.smh-msg-cont span {
	width: 100%;
	height: 100%;
	text-align: center;
	font-size: 4vw;
	text-shadow: 1px 1px 3mm #000;
	line-height: 40vh;
}

@media screen and (min-width:1200px) {

	.cover-list li,
	.idx-mc-cont li,
	.latest-list li,
	.book-list li {
		width: calc(100% / 6 - 4mm - 1px);
	}

	.cover {
		height: 18vw;
	}
}

@media screen and (min-width:1600px) {

	.cover-list li,
	.idx-mc-cont li,
	.latest-list li,
	.book-list li {
		width: calc(100% / 8 - 4mm - 1px);
	}

	.cover {
		height: 16vw;
	}
}

@media screen and (min-width:2200px) {
	.w998 {
		max-width: 2110px;
		margin: 0 auto;
	}

	.update-cont ul {
		width: 2110px;
		min-width: 1000px;
	}

	.cover-list li,
	.idx-mc-cont li,
	.latest-list li,
	.book-list li {
		width: calc(100% / 10 - 4mm - 1px);
	}

	.cover {
		height: 12vw;
	}
}

@media screen and (min-width: 140vh) {
	.tbCenter {
		max-width: 140vh;
	}

	#mangaMoreBox img {
		border-radius: 0;
	}
}
}
