css:
@-moz-document url-prefix("http://jira"), url-prefix("https://jira"), domain("jira.atlassian.com") {
.ghx-has-swimlanes .ghx-issue-subtask,
.ghx-issue-subtask {
    height: 30px !important;
    font-size: 10px !important;
}

.ghx-has-swimlanes .ghx-issue-subtask .ghx-summary .ghx-inner,
.ghx-issue-subtask .ghx-summary .ghx-inner {
    overflow: hidden !important;
    white-space: nowrap !important;
    text-overflow: ellipsis !important;
}

.ghx-has-swimlanes .ghx-issue-subtask .ghx-avatar,
.ghx-issue-subtask .ghx-avatar {
    top: 5px !important;
}

.ghx-has-swimlanes .ghx-issue-subtask .ghx-avatar .ghx-avatar-img,
.ghx-issue-subtask .ghx-avatar-img {
    height: 20px !important;
    width: 20px !important;
}

.ghx-has-swimlanes .ghx-issue-subtask .ghx-type,
.ghx-has-swimlanes .ghx-issue-subtask .ghx-days,
.ghx-has-swimlanes .ghx-issue-subtask .ghx-flags,
.ghx-has-swimlanes .ghx-issue-subtask .ghx-extra-fields,
.ghx-issue-subtask .ghx-type,
.ghx-issue-subtask .ghx-days,
.ghx-issue-subtask .ghx-flags,
.ghx-issue-subtask .ghx-extra-fields {
    display: none !important;
}

.ghx-has-swimlanes .ghx-issue-subtask .ghx-end,
.ghx-issue-subtask .ghx-end {
    display: none !important;
}

.ghx-has-swimlanes .ghx-issue-subtask .ghx-issue-content, 
.ghx-issue-subtask .ghx-issue-content {
    padding: 3px 10px !important;
}
}
