css:
@-moz-document domain("countle.com") {
.appArea, .leaderboard, .about2Back, .fill {
    background-color: #161616;
}
.skyscraper {
    display: none;
}
.unit {
    border: 1px solid #000000;
    background-color: #101010;
}
body, html, #rlink, .bloglink, .input, textarea {
    color: #777777;
}
body, html{
    background-color: #000000;
}
a {
    color: #969696;
}
.top, .footer, #submit {
    background-color: #004fad;
}
#submit {
    border: none;
    border-bottom: none;   
}
.input, textarea, #link {
    border: 1px solid #000000;
    background-color: #292A2E;
}
}
