css:
@-moz-document domain("dm5.com")
{

iframe,
footer,
#cp_footer,
#nAd,
#last-win .view-rec,
#last-win .index-manga-list,
.cat-filter .footer-btn,
.guide,
.ad_360_item,
.today,
.view_user,
.clDiv,
.sitemaji_banner,
.topBar>a:last-child,
.detailForm>div:nth-child(3),
.floatBar_1,
.finalPage .info,
.new-tip,
.bottom_fix_ad,
.phone,
.header-search,
a[href="#"],
a[href="/vipopen/"],
a[href="javascript:showTip(3)"],
a[href="javascript:showTip(5)"],
a[href*="manhuaren://"],
img[src$="view_code_ad.jpg"],
.view-comment-main .list li.solid:last-child,
body>.view-paging[style="margin-top:5px;"] {
	display: none !important;
}

body, div, dl, dt, dd, ul, ol, li, h1, h2, h3, h4, h5, h6, pre, form, fieldset, input, textarea, p, blockquote, th, td {
	font-family: sans-serif
}

body {
	min-width: 900px;
	background: linear-gradient(93deg, #222 0, #444 45%, #444 55%, #222 100%)
}

body.white {
	background: linear-gradient(93deg, #bbb 0, #ddd 45%, #ddd 55%, #bbb 100%)
}

img {
	object-fit: contain
}

.header-bar {
	position: absolute;
	right: 50px;
	float: none;
}

.container-fluid {
	min-width: 900px;
}

body>div[class^="index-"] {
	max-width: 1250px;
	margin: 0 auto;
}

.full-class {
	max-width: 1220px;
	margin: 0 auto;
}

.index-rank {
	width: 33%;
}

.index-cooperation-list li {
	margin: 5mm;
}

img[src$="/cross_red.png"] {
	top: -4mm !important;
	right: -4mm !important;
	border-radius: 50%;
	box-shadow: 0 0 0 1mm red, 0 0 0 2mm #fff;
	zoom: .9
}

#showimage,
#barChapter {
	position: relative;
	max-width: 150vh;
	min-height: 96vh !important;
	margin: 0 auto;
	padding: 3mm 0;
	text-align: center;
	direction: rtl;
	font-size: 0;
}

.coverForm,
.coverForm img {
	max-height: 600px
}

.readPage {
	background: #777;
	padding: 3mm 0 0
}

#cp_img {
	text-align: center;
}

#cp_image,
#cp_img>img {
	width: auto !important;
	min-width: 60vh;
	max-width: calc(100% - 4mm) !important;
	height: calc(100vh - 11mm) !important;
	margin: 2mm auto !important;
	border-radius: 2mm;
	box-shadow: 0 0 3mm #666;
}

.view-main-1 {
	padding: 4mm 0 0;
}

.rightToolBar,
.white .rightToolBar {
	bottom: 40px !important;
	height: auto;
	padding: 1mm 2mm;
	border-radius: 8mm;
	opacity: .3;
	transition: .15s;
}

.rightToolBar:hover {
	opacity: 1;
}

.rightToolBar a,
.white .rightToolBar a {
	margin: 1mm;
}

.rightToolBar a.logo_3 {
	display: none !important;
}

.readPage .readForm {
	direction: rtl;
	text-align: center
}

.readPage .readForm img {
	display: inline-block;
	min-width: 80%;
	width: auto !important;
	max-width: calc(100vw - 2mm) !important;
	max-height: calc(100vh - 9mm) !important;
	margin: 2.22mm 1mm;
	border-radius: 2mm;
	box-shadow: 0 1mm 3mm black
}

.readPage .readForm img:first-child {
	display: block;
	margin: 2.22mm auto !important
}

.header {
	background: rgba(255, 255, 255, .5);
	box-shadow: 0 0 2mm rgba(0, 0, 0, .4);
	backdrop-filter: blur(6mm);
	-webkit-backdrop-filter: blur(6mm)
}

.header:hover {
	background: rgba(255, 255, 255, .9);
}

.view-header-2 {
	position: fixed !important;
	border-bottom: 1px gray solid;
	top: -57px !important;
	text-shadow: 0 0 3mm #000;
	box-shadow: 0 0 2mm rgba(0, 0, 0, .5);
	background: rgba(200, 200, 200, .4);
	backdrop-filter: blur(2mm);
	-webkit-backdrop-filter: blur(2mm);
	transition: all .8s .5s;
}

.view-header-2:hover {
	background: rgba(0, 0, 0, .6);
	transition: all .2s 0s;
}

body.white .view-header-2 {
	text-shadow: 0 0 3mm #fff;
	background: rgba(0, 0, 0, .3);
	backdrop-filter: blur(2mm);
	-webkit-backdrop-filter: blur(2mm);
}

body.white .view-header-2:hover {
	background: rgba(240, 240, 240, .85);
}

.view-header-2 .right-bar {
	right: 10mm;
}

.view-header-2 a,
.view-header-2 .right-bar .bookshelf,
.view-header-2 .right-bar .collection,
.view-header-2 .right-bar .read-style {
	color: #bbb;
}

body.white .view-header-2 a,
body.white .view-header-2 .right-bar .bookshelf,
body.white .view-header-2 .right-bar .collection,
body.white .view-header-2 .right-bar .read-style {
	color: #444;
}

.view-header-2>.title {
	text-align: left;
	margin-left: 9em;
	margin-right: 23em;
	text-overflow: ellipsis;
	overflow: hidden;
	white-space: nowrap;
}

.view-header-2 .title .right-arrow {
	background-position: 6px 50% !important;
}

.view-header-2>.title span {
	float: left;
	max-width: 36%;
}

.view-header-2>.title a {
	float: left;
	max-width: 100%;
	overflow: hidden;
	text-overflow: ellipsis;
}

.view-fix-top-bar {
	position: fixed;
	top: -2em !important;
	height: 3em;
	line-height: 3em;
	opacity: .3;
	box-shadow: 0 0 0 1px #888, 0 0 5px #444;
	transition: all .8s !important;
}

.view-fix-top-bar-title {
	height: 3em;
	line-height: 3em;
	padding-left: 3em;
}

.view-fix-top-bar-back,
.view-fix-top-bar-right-logo {
	height: 1.4em;
	top: 0;
	padding: .8em 2mm;
	margin: 0;
}

.vPage .topToolBar .center,
.vPage .topToolBar .center a {
	color: #ccc;
	display: inline-flex;
	text-overflow: ellipsis;
	overflow: hidden;
	white-space: nowrap;
}

.readPage .titleBar {
	top: -30px !important;
	opacity: .3 !important;
	transition: all .8s !important;
}

body>.view-header-2:hover,
.readPage .titleBar:hover,
.view-fix-top-bar:hover {
	top: 0 !important;
	opacity: 1;
	transition: all .2s 0s;
}

.readPage .toolBar {
	width: 100vw;
	position: fixed !important;
	bottom: -37px !important;
	opacity: .2 !important;
	transition: all .8s .5s;
}

.view-fix-bottom-bar {
	bottom: -47px !important;
	box-shadow: 0 0 0 1px #888, 0 0 5px #444;
	opacity: .3 !important;
	transition: all .8s .5s;
}

.readPage .toolBar:hover,
.view-fix-bottom-bar:hover {
	bottom: 0 !important;
	opacity: .95 !important;
	transition: all .2s;
}

.readPage .readTipForm {
	background: navy;
	padding-bottom: 8mm;
}

.am-scrollable-horizontal {
	width: 50vw;
	margin: 0 auto
}

.am-avg-sm-4>li {
	width: auto;
	min-width: 6em
}

.am-avg-sm-4>li:nth-of-type(4n+1) {
	clear: none
}

.am-avg-sm-4>li>a {
	transition: all .3s
}

.am-avg-sm-4>li>a:hover {
	transition: all .3s;
	background: #DDF
}

.am-avg-sm-4>li>a:active {
	box-shadow: inset 0 0 2mm gray
}

div[style=" margin-top:0px; height:35px;border-bottom:none;margin-bottom:-6px;"] {
	background: #888;
	width: 100vw !important;
	padding: 5px;
	position: fixed;
	bottom: -30px;
	opacity: .2;
	transition: all .8s;
}

div[style=" margin-top:0px; height:35px;border-bottom:none;margin-bottom:-6px;"]:hover {
	bottom: 0;
	opacity: .9;
	transition: all .2s
}

.v_yan1,
.v_yan2,
.search_fy a {
	transition: all .2s
}

.v_yan1,
.v_yan2 {
	width: 6em !important;
	margin: 0 1em;
}

.search_fy {
	padding: 0 2em;
}

.vPage.white .commentList .list li .info p.c {
	font-size: 11pt;
}

#goBak {
	cursor: pointer;
	padding: .6em 1em;
	margin-top: -11px;
	border-radius: 1.5mm
}

.topBar img.backlogo {
	width: 48px;
	height: 36px;
	padding: 8px;
	margin-top: -13px;
	border-radius: 1.5mm
}

.readPage .toolBar li {
	color: #aaa;
}

.readPage .toolBar li img {
	cursor: pointer;
	padding: .2em 45% 1.8em 45%;
	margin: .2em 0 -1.7em 0;
	border-radius: 1.5mm;
	background: transparent;
	transition: background-color .8s;
}

.readPage .selectBar .chapterList {
	height: auto;
}

.readPage .selectBar .chapterList li {
	width: 4em;
	margin: .5em;
}

#goBak:hover,
.topBar img.backlogo:hover,
.readPage .toolBar li img:hover,
.readPage .selectBar .chapterList li a:hover {
	display: inline-block;
	background: rgba(255, 255, 255, 0.3);
	transition: background-color .2s;
}

#goBak:active,
.topBar img.backlogo:active,
.readPage .toolBar li img:active,
.readPage .selectBar .chapterList li a:active {
	background: #5a3;
	box-shadow: inset 0 0 1mm black;
}

body>.view-paging {
	width: 100vw;
	height: 30px;
	margin: 0 !important;
	padding: 8px 0 !important;
	background-color: rgba(200, 200, 200, .3);
	position: fixed;
	bottom: -32px;
	left: 0;
	z-index: 3;
	box-shadow: 0 0 0 1px #888;
	backdrop-filter: blur(3mm);
	-webkit-backdrop-filter: blur(3mm);
	transition: all .8s .5s;
}

body.white>.view-paging {
	background-color: rgba(30, 30, 30, .3);
	backdrop-filter: blur(2mm);
	-webkit-backdrop-filter: blur(2mm);
}

body>.view-paging:hover {
	bottom: 0;
	background-color: rgba(180, 180, 180, .7);
	transition: all .2s;
}

body.white>.view-paging:hover {
	background-color: rgba(30, 30, 30, .5);
}

.view-bottom-bar {
	padding: 0 0 4mm;
}

.view-bottom-bar-item {
	margin: 0 5px 0 0;
	height: 30px;
	line-height: 30px;
}

.left-bar,
.view-comment-main {
	margin: 3mm;
}

.view-comment-main {
	border-radius: 2mm;
	overflow: hidden;
	background: rgba(128, 128, 128, .2);
	border: none;
	box-shadow: 0 0 0 1px rgba(255, 255, 255, .3), 0 0 3mm rgba(0, 0, 0, .4);
}

body.white .view-comment-main {
	background: rgba(255, 255, 255, .8);
	border: none;
}

.view-comment-main .top {
	line-height: 3em;
	height: 3em;
	margin-bottom: 1.5mm;
}

.view-comment-main .top .page {
	margin-top: 2mm;
	line-height: 2em;
}

.view-comment-main .top .page a,
.view-comment-main .top .page span {
	margin: 0 0 0 1.5mm;
}

.view-comment-main .list {
	width: 100%;
}

.view-comment-main .list li .info .comment-item {
	padding-top: 1em;
}

.view-comment-main .list li .info .content {
	font-size: 14pt !important;
	color: #aaa;
	margin: 0 !important;
}

.view-comment-main .list li .info {
	width: calc(100% - 100px);
}

.view-comment-main .list li .info .bottom {
	text-align: right;
	font-size: 12pt;
	line-height: 1.2;
	color: #aaa;
	margin: 1mm 0 0 !important;
}

.view-comment-main .list li .info .bottom .right .zan,
.view-comment-main .list li .info .bottom .right .comment {
	margin-left: 4mm;
	color: #aaa;
}

body.white .view-comment-main .list li .info .content {
	color: #555;
}

body.white .view-comment-main .list li .info .bottom {
	font-size: 12pt;
	color: #aaa;
}

body.white .view-comment-main .list li .info .bottom .right .zan,
body.white .view-comment-main .list li .info .bottom .right .comment {
	margin-left: 4mm;
	color: #aaa;
}

.view-comment-main .list li .info .bottom .right {
	margin-left: 2mm;
}

.view-comment-main .list li .info .bottom .right a {
	padding: 1px 0;
}

.view-comment-main .list li {
	width: 100%;
	border: none;
	padding: 5px 0 2px !important;
}

.view-comment-main .list li.dashed {
	width: 100%;
	background: linear-gradient(180deg,
			rgba(200, 200, 200, .8) 0,
			transparent 1pt);
}

.view-comment-main .list li.solid,
.view-comment-main .list li.dashed .info {
	padding: 1px 0 0 !important;
	border: none !important;
}

.view-comment-main .list li .info .title {
	margin-top: 0;
}

.view-comment-main .list li .cover img {
	background-color: #eee;
	border-radius: 2mm;
	box-shadow: 0 0 0 1px #666;
}

body.white .view-comment-main .list li .cover img {
	box-shadow: 0 0 0 1px #ccc;
}

.view-comment-main .bottom-bar {
	padding: 3mm 0 !important;
	background-color: rgba(33, 33, 33, .6);
}

body.white .view-comment-main .bottom-bar {
	background-color: rgba(220, 220, 220, .6);
}

#last-win {
	border-radius: 3mm;
}

.view-comment {
	padding: 0 0 2em;
	margin: 0;
	background-color: rgba(0, 0, 0, .3);
}

.banner_detail_form {
	width: auto;
	max-width: 1200px;
	background: #ddd;
	padding: 2mm;
}

.banner_detail_form .cover img {
	object-fit: cover
}

.view-comment>.container:not([style]) {
	width: 1200px;
	background: #ddd;
	padding: 0 4mm 4mm;
	border-radius: 0 0 2mm 2mm;
}

.view-comment .left-bar {
	width: calc(100% - 270px);
}

.view-comment .view-win-list {
	width: auto;
	overflow: visible;
}

.view-comment .view-win-list li {
	float: none;
	display: inline-block;
	width: calc(100% / 4 - 2mm);
	line-height: 1.8em;
	height: 1.8em;
	text-align: center;
	border-radius: 5mm;
	margin: 1.5mm 0 0 0;
	padding: 0 2mm;
	transition: .1s;
}

.view-comment .view-win-list li:hover {
	background: #f2edc2;
	box-shadow: 0 0 0 1px rgba(80, 50, 20, .4);
}

.chapteritem {
	display: inline;
}

.view-comment .view-win-list li a {
	padding: 0;
}

.view-comment .view-win-list li:hover a {
	color: #229;
}

#barChapter img {
	margin: 3mm auto !important;
	width: 66vh !important;
	height: auto !important;
	max-width: calc(100vw - 4mm) !important;
}

#barChapter img:not(:first-of-type) {
	display: inline !important;
}

.view-win .view-win-list li {
	text-align: center;
	width: calc(100%/3 - 4mm);
	background: transparent;
	padding: 0;
	margin: 2mm;
	height: auto;
}

.view-win .view-win-list li a {
	white-space: nowrap;
	overflow: hidden;
	background: #f3f3f3;
	text-overflow: ellipsis;
	padding: 0 .6em;
	line-height: 2.2;
	border-radius: 2em;
	box-shadow: 0 0 0 1px #aaa;
}

.container {
	width: calc(100% - 13mm);
	min-width: 900px;
	max-width: 2000px;
}

.header>.container {
	position: relative;
}

.cat-filter {
	max-height: 85px;
	overflow: scroll;
	border: none;
	border-radius: 2mm;
	box-shadow: inset 0 0 3mm 1px #aaa;
	transition: max-height .5s .2s, background-color .5s;
}

.cat-filter:hover {
	background: #e8eaf2;
	max-height: 270px;
	transition: max-height .5s .5s, background-color .3s;
}

.view-comment-main .list li .info textarea {
	width: calc(100% - 9px) !important;
	height: 2.8em;
	font-size: 13pt;
	color: #eee;
	background: #444;
	padding: 1mm;
	margin: 0;
	border-radius: 5px;
	border: 1px solid #666;
	transition: .4s .6s;
}

body.white .view-comment-main .list li .info textarea {
	color: #333;
	background: #ddd;
	border: 1px solid #bbb;
}

.view-comment-main .list li .info textarea:focus {
	height: 5em;
	transition: .4s .6s;
}

.view-comment .view-comment-sub .avatar-bar,
.view-comment .view-comment-sub .sub-manga {
	margin-top: 10px;
	border-radius: 2mm;
}

body:not(.white) .view-comment .view-comment-sub .sub-manga {
	background: #333;
}

body:not(.white) .view-comment .view-comment-sub .sub-manga .top,
body:not(.white) .view-comment .view-comment-sub .sub-manga .list li .info .title a,
body:not(.white) .view-comment .view-comment-sub .sub-manga .list li .info .subtitle-new,
body:not(.white) .view-comment .view-comment-sub .sub-manga .list li .info .tip {
	color: #bbb;
}

body:not(.white) .view-comment .view-comment-sub .sub-manga,
body:not(.white) .view-comment .view-comment-sub .sub-manga .top,
body:not(.white) .view-comment .view-comment-sub .sub-manga .list li {
	border-color: #666;
}

.box .mh-list.col7 {
	width: 100%;
}

.box .mh-list.col7>li {
	height: auto;
	margin: 2mm;
	transition: .2s;
}

.view-comment .mh-list.col7>li {
	width: calc(100%/5 - 3mm);
	height: auto;
	margin: 0 3mm 3mm 0;
}

.mh-item {
	border-radius: 1.5mm;
	box-shadow: 0 0 1mm #888;
}

.mh-cover {
	border-radius: 1.5mm 1.5mm 0 0
}

.mh-list .mh-item-detali {
	background: #e3e3e3;
	overflow: hidden;
	padding: 0 1.5mm 2mm;
	border-radius: 0 0 1.5mm 1.5mm
}

.mh-item .mh-item-detali .title {
	text-align: center;
	padding: 1mm 0 2mm 0;
	margin: 0 !important;
}

.mh-item .mh-item-detali .title a {
	display: inline-block;
	max-width: calc(100% - .3em);
	font: 600 11pt sans-serif;
	line-height: 1.3em;
	color: #448;
	overflow: hidden;
	text-overflow: ellipsis;
	padding: 0 .4em;
	border-radius: 1em;
	transition: .5s;
}

.mh-item .mh-item-detali .title a:hover {
	color: navy;
	background: rgba(255, 255, 255, .6);
	box-shadow: 0 1px 1mm 1px #777;
	transition: .1s;
}

.mh-item .mh-item-detali .title a:active {
	color: #eef;
	background: #aaa;
	box-shadow: inset 0 .3mm 1mm #000;
	transition: all .2s;
}

.mh-item .mh-tip-wrap {
	top: -10%;
	max-height: none;
	padding: 0;
	overflow: hidden;
	border-radius: 2mm;
	transition: width 0s, opacity .2s, transform .2s, top .2s;
}

.mh-item .mh-tip-wrap.active {
	top: 0;
	width: 226px;
	height: 330px;
	box-shadow: 0 0 5mm #555;
}

.mh-item-tip {
	margin: 0 auto;
}

.view-win {
	box-shadow: 0 2px 5mm rgba(0, 0, 0, .4);
}

.view-mask {
	opacity: .6;
	visibility: visible;
	transition: opacity .3s, visibility 0s;
}

.view-paging a,
.view-paging span {
	font-size: 11pt;
	background: #444;
	border-radius: 6px;
	box-shadow: inset 0 0 0 1px rgba(0, 0, 0, .4);
}

.view-comment-main .child-list {
	margin: .3em 0 !important;
	background: rgba(155, 155, 155, .1) !important;
	box-shadow: 0 0 0 1px rgba(255, 255, 255, .2);
}

body.white .view-comment-main .child-list {
	background: rgba(100, 100, 100, .1) !important;
	box-shadow: 0 0 0 1px rgba(0, 0, 0, .2);
}

.box-con-5n1 .detail-tabs {
	right: 5px;
}

.box-con-5n1 .detail-tabs-container .detail-tabs-pane {
	width: 100%;
	padding: 8px;
}

.box-con-8n3 .box-8n3-pane {
	background-color: #333;
}

body.white .box-con-8n3 .box-8n3-pane {
	background-color: #eee;
}

.box-con-8n3 .box-8n3-pane .box-cat-list li {
	margin-bottom: 3mm;
}

.js_box_pane {
	padding: 3mm;
	border-radius: 2mm;
	box-shadow: 0 0 2mm gray;
}

.article-main {
	width: calc(100% - 250px);
}

.page-pagination ul li a {
	background: #eee;
}

.alertTop_1 {
	height: 4em;
}

.index-right-float {
	margin-left: calc(50vw - 30mm);
}

.view-win .title .view-code,
.view-win .title .view-collection {
	padding: 3px 0 3px 24px;
}

.view-win .title .view-btn-back {
	background: #f5f5fc;
	border: none;
}

.view-win .title .view-btn-back,
.view-win .title .view-btn-next {
	border-radius: 5px;
	box-shadow: 0 0 0 1px #bbb;
}

.view-win .title .view-btn-back:hover,
.view-win .title .view-btn-next:hover {
	box-shadow: inset 0 0 0 1px #ddd, 0 0 5px #22c;
}

.new-search-banner {
	height: auto;
}

.new-search-input-form {
	margin: 5mm auto 2mm;
	height: auto;
}

.new-search-input-form input,
.new-search-input-form-btn-1,
.new-search-input-form-btn-2 {
	height: auto;
	line-height: 2;
	vertical-align: middle;
