css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("startpage.com") {
.cloud, .navbar .navbar-header, .navbar {
    background: rgba(0, 0, 0, 0) none no-repeat scroll center center !important;
}
    #main_link h1.first a.addsettings {
        background: url(https://i.imgur.com/WKIgdg5.jpg);
        height: 133px;
        width: 413px;
        display: block;
    }
    a.navbar-brand {
        background: url(https://i.imgur.com/lhAlYLs.jpg) no-repeat center center;
        height: 45px;
        width: 140px;
        display: block;
    }
    .navbar navbar-default, .stick-header, .lemmon-slider-wrapper {
        background-color: #fff!important;
    }
    a.addsettings img, a.navbar-brand img, #enhanced, #add_to_browser, #set_as_home, #addtobrowser, #setashome, div.intro, div.extended-scrollbtn-text, span.start-down-icon {
        display: none;
    }
    .go-btn input#submit1 {
       background: #d8d8d8 none repeat scroll 0 0 !important;
       border: 2px outset #000;
       border-radius: 1px!important;
       color: #000;
    }
    .go-btn input#submit1:focus {
       border: 2px inset #000;
    }
    .go-btn input#submit1, div#search input#submit1, div#search_bottom input#submitb {
       color: #000;
    }
    .small-go-btn {
       background: #d8d8d8 none repeat scroll 0 0 !important;
       border: 2px outset #000;
       border-radius: 1px!important;
       color: #000;   
       margin: -1px 0 0 60px;
    }
    div#search input#submit1:focus, div#search_bottom input#submitb:focus {
       background: #d8d8d8 none repeat scroll 0 0 !important;
       border: 2px inset #000;
    }
    .first input#query, search-field, #field_size_bottom, #field_size {
       border: 2px inset #000 !important;
       border-radius: 0px!important;;
    }
}
