css:
@-moz-document domain("v3rmillion.net") {
#wrapper {
    background: #02222B;
}
.tcat {
    background: #02181f;
}
.pm_new,
.alert_new {
    background: #125f75!important;
}
#logo img {
    content: url("https://i.imgur.com/fdepDia.png")
}
.inline_row a,
.inline_row a:visited {
    color: #fff;
}
#content,
.navigation,
#header {
    background: #02181f;
    border: 1px solid #02181f;
}
ul.menu li a.active,
ul.menu li ul li a:link,
ul.menu li ul li a:visited {
    background: #125f75!important;
}
.panel_buttons {
    background: #125f75;
    border-top: 5px solid #125f75;
}
.trow_sep {
    background: #125f75;
    color: #ffffff;
}
ul.menu li ul {
    background: #125f75;
    border-top: 5px solid #125f75;
}
.ddm_anchor:hover,
.ddm_anchor a:hover,
ul.menu li a:hover,
ul.menu li:hover {
    background: #125f75!important;
}
.usercp_nav_item:before,
.modcp_nav_item:before {
    color: #125f75;
}
.profile_header {
    background: #02222b;
}
fieldset {
    border: 1px solid #125f75;
}
.thead input.textbox,
.thead select {
    border: 1px solid #125f75;
}
#bridge {
    background: #02222B;
    border-bottom: 5px solid #125f75;
}
.trow1,
.trow2 {
    background: #02222b;
    border-bottom: 1px solid #02181f;
}
.forum_on,
.cRem a:visited,
.cRem a:link {
    color: #125f75!important;
}
.forum_off {
    color: #636363;
}
.thead,
.tfoot {
    background: #125f75;
}
.buttons {
    background: #02181f;
    border: 1px solid #02181f;
}
.buttons {
    background-color: #02181f!important;
    border: 1px solid #125f75!important;
}
a.button:link,
a.button:visited,
a.button:active,
.pagination a,
button,
input.button,
.postbit_buttons>a:link,
.postbit_buttons>a:hover,
.postbit_buttons>a:visited,
.postbit_buttons>a:active {
    background: #125f75;
    border: 1px solid #125f75;
}
.thread_head {
    background: #02222B!important;
    border-bottom: 5px solid #125f75!important;
}
.post .post_head {
    border-top: 15px solid #02222B;
}
.post .post_author {
    border-bottom: 2px solid #02222B;
    background: #02222B;
}
blockquote {
    border: 1px solid #02181f;
    background: #011015;
}
blockquote cite {
    border-bottom: 1px solid #02222b;
}
.post .post_author div.author_statistics {
    background: #031c23!important;
}
.post_controls {
    background: #02222B;
    border-bottom: 1px solid #02222B;
    border-top: 3px solid #02222B;
}
ul.bottommenu {
    background: #02222B;
}
.tborder {
    background: #01151b;
}
#footer {
    background: #02181f;
    border-top: 5px solid #125f75;
}
}
