css:
@-moz-document regexp("https?://.*literatureandlatte\\.com/forum.*") {
/* L&L forums loads the Effra font family but then does not consistently apply it, leaving
a mish mash of Verdana, Tahoma, Lucida, Effra and others in various places. This userstyle 
returns Effra back to its rightful place!!!
*/

html, body {
	font-size: 17px !important;
	font-family: Effra-Light, "Avenir Next", Helvetica, sans-serif !important;
}
h1, h2, h3, h4 {
	font-family: Effra-Regular, "Avenir Next", Helvetica, sans-serif !important;
}
.content, .postbody {
	font-size: 17px !important;
	font-family: Effra-Light, "Avenir Next", Helvetica, sans-serif !important;
}
textarea, input {
	font-family: Effra-Regular, "Avenir Next", Helvetica, sans-serif !important;
}
.forum ul.forums .row, .forum ul.topiclist .row, .forum ul.topics .row {
	font-size: 14px;
	line-height: 18px;
}
.postprofile .username {
	font-size: 18px;
}
code, kbd, pre, samp {
	font-family: "Fira Code", Menlo, Consolas, monospace, serif !important;
	font-size: 12px;
}
strong {
	font-weight: 600 !important;
}
}
