css:
@-moz-document domain("www.youtube.com") {
ytd-guide-section-renderer {
	--paper-item-min-height: 30px;
}
.guide-icon.ytd-guide-entry-renderer {
    margin-right: 14px;
}
yt-img-shadow.ytd-guide-entry-renderer {
    margin-right: 14px;
}
}

@-moz-document url-prefix("https://www.youtube.com/playlist") {
#index.ytd-playlist-video-renderer,
#content.ytd-playlist-video-renderer {
    padding: 4px 0;
}
.pl-video .pl-video-thumbnail,
.pl-video td, .pl-video .more-menu-wrapper {
    padding-top:    10px;
    padding-bottom: 10px;
}
}
