css:
@-moz-document url-prefix("https://developertracker.com/escape-from-tarkov/") {
body {
    background-color: #0e0e0e;
    color: #c5b88e
}
a {
    color: #827356
}
a:hover {
    color: #8e7f61
}
a.ipsAttachLink_image {
    display: flex;
    justify-content: center
}
h1 a,
h1 a:active,
h1 a:hover {
    color: #c5b88e
}
.services-wrapper {
    color: #827356
}
.services-wrapper label:hover {
    text-decoration: underline
}
.checkbox-inline input[type=checkbox]:after,
.checkbox input[type=checkbox]:after,
input[type=checkbox]:after {
    border-color: #5f5440
}
.checkbox-inline input[type=checkbox]:focus:after,
.checkbox input[type=checkbox]:focus:after,
input[type=checkbox]:focus:after {
    border-color: #5f5440
}
.checkbox-inline input[type=checkbox]:checked:after,
.checkbox input[type=checkbox]:checked:after,
input[type=checkbox]:checked:after {
    background-color: #5f5440;
    border-color: #5f5440
}
.checkbox-inline input[type=checkbox]:checked:before,
.checkbox input[type=checkbox]:checked:before,
input[type=checkbox]:checked:before {
    border-color: #d6d0bd
}
.has-feedback input[type=text] {
    box-shadow: inset 0 -1px 0 #827356;
    color: #c5b88e
}
.has-feedback input[type=text]:focus {
    box-shadow: inset 0 -2px 0 #d6d0bd
}
.has-feedback input[type=text]::placeholder {
    color: #6d654d
}
.panel {
    background-color: #171715
}
.panel-default > .panel-heading {
    color: #dad1b6;
    background-color: #21211f
}
.panel-default > .panel-footer {
    background-color: #171715
}
blockquote {
    border-color: #827356
}
.expander {
    background: linear-gradient(180deg, hsla(0, 0%, 100%, 0) 0, #1f1d18);
    text-align: center
}
.btn {
    background-color: #c5b88e
}
.btn-default:active:hover,
.btn-default:hover {
    background-color: #8e7f61
}
iframe {
    border: 1px solid
}
.panel svg.icon,
img.loader {
    filter: sepia(100%)
}
}
