css:
@-moz-document url-prefix("https://www.trillian.im/web/4.0/") {
.contact .name {
	width : 112px !important;
}
.contact .name:before {
	content : 'Contact Name ';
}
.avatar {
	overflow : hidden;
	width    : 40px;
}
.avatar:before {
	content : 'CN';
	display : block;
}
.avatar.avatar30 {
	overflow : hidden;
	width    : 30px;
}
#x_chatName {
	overflow : hidden;
	width    : 182px;
}
#x_chatName:before {
	content : 'Contact Name ';
}
.right .message .text {
	overflow    : hidden;
	height      : 12px;
}
.right .message .text:before {
	content : 'Outgoing Message';
	display : block;
}
.right .message.incoming .text:before {
	content : 'Incoming Message';
}
.right .message .name {
	overflow : hidden;
	height   : 17px;
}
.right .message .name:before {
	content : 'Contact Name XX Month Name XXXX HH:MM';
	display : block;
}
#historyTimeline .message .youtube, .right .message .youtube {
	display : none;
}
.avatar.avatar30 img ,
#x_identityAvatar img {
	display : none;
}
.avatar.avatar30:not(.empty) ,
#x_identityAvatar:before {
	background-color : #0071c7;
	border-radius    : 30px;
	content          : 'CN';
	color            : #FFF;
	font-size        : 13px;
	height           : 30px;
	text-align       : center;
	line-height      : 50px;
	padding          : 7px;
	width            : 30px;
}
.avatar.avatar30:not(.empty) {
	line-height : 30px;
	padding     : 0px;
}
#x_identityName {
	overflow : hidden;
	width    : 95px;
}
#x_identityName:before {
	content : 'Contact Name';
	display : block;
}
.right .daily_timestamp {
	overflow : hidden;
	height   : 30px;
}
.right .daily_timestamp:before {
	content : 'Dayname ';
	display : block;
}




}

@-moz-document domain("trillian.im") {
/* viewable on the whole domain */
}
