css:
@-moz-document domain("pikabu.ru") {
.header_dark .header__main {
    background-color: #8ac858!important;
}
body {														
    background: url("https://avatanplus.com/files/resources/original/574dd6f3aca26155080fa7fb.jpg") no-repeat fixed !important;
}
.comment__user[data-own=true] .user__nick {
    background-color: rgba(138, 200, 88, 0)!important;
    color: #8ac858!important;
    padding: 0px 0px!important;
}
.comment__user[data-own-story=true] .user__nick {
    background-color: #8ac858!important;
    color: #ffffff!important;
    padding: 0px 8px!important;
}
.story_tags-at-top .story__user-at-top+.story__tags .tags__tag_highlight {
    color: #ffffff!important;
    padding: 0px 2px!important;
}
.tags__tag_highlight,
.tag_highlight {
    color: #03A9F4 !important;
    background-color: #4CAF50 !important;
    border-color: #4CAF50;
    border-radius: 2px;
    box-shadow: 0px 0px 0px 2px #4caf50;
}
.icon--ui__strawberry {
    color: #8ac858;
    fill: #fd5d47;
 	width: 18px!important;
    height: 17px!important;
}
a.tags__tag[href="/tag/%D0%B6%D0%B5%D1%81%D1%82%D1%8C/hot"] {
    color: #fff!important;
    background: #F44336!important;
    padding: 0px 2px!important;
    border-radius: 2px;
    box-shadow: 0 0 0px 2px #F44336;
}
a.tags__tag[href="/tag/%D1%84%D0%B5%D0%B9%D0%BA/hot"] {
    color: #fff!important;
   background: #F44336!important;
    padding: 0px 2px!important;
    border-radius: 2px;
    box-shadow: 0 0 0px 3px #F44336;
    font-size: 14px;
}
}
