css:
@-moz-document domain("wip.chat") {
  html {
    height: 100%;
  }
  html,
  body,
  .headerWrap,
  .wrapper,
  .postDeck,
  .contentBlock,
  .answer,
  .todoDeck,
  .userCard,
  #product_pitch {
    background-color: black;
  }
  a.contentButton:link,
  a.contentButton:visited {
    background-color: #112233;
  }
  .header a.header__link:not(.header__link--button):hover,
  .links,
  .wrapper > section > ol > li > a,
  .wrapper > section > p > a {
    background-color: #2a7dd2;
    color: white;
  }
  .main,
  .postDeck,
  .contentBlock,
  .user .user__details .user__details__fullName .user__details__fullName__name,
  .contentBlock .contentBlock__description,
  .answer,
  .navBar .navBar__tab:link,
  .navBar .navBar__tab:visited,
  .userCard > .userCard__stats > a:link,
  .userCard > .userCard__stats > a:visited,
  a,
  a:link,
  a:visited,
  a.contentButton:link,
  a.contentButton:visited,
  .logo .logo__name,
  .header a.header__link,
  textarea {
    color: white;
  }
  .product__details__name,
  a.badge.badge--streak,
  a.comment__body__mention,
  .navBar .navBar__tab--active:link,
  .navBar .navBar__tab--active:visited {
    color: black;
  }
  a.contentButton:link,
  a.contentButton:visited {
    border: 1px solid white;
  }
}
