css:
@-moz-document domain("github.com") {
  .timeline-new-comment,
  .container-lg,
  .container-xl,
  .container {
    max-width: none !important;
  }

  .vis {
    width: 100% !important;
  }

  .container-lg {
    margin-left: 4%;
    margin-right: 4%;
  }

  .page-responsive .container-lg .col-md-9 {
    display: flex;
    margin: auto;
    flex-direction: column;
    width: 90%;
  }

  .container {
    width: auto !important;
    margin-left: 4%;
    margin-right: 4%;
    padding-left: 15px;
    padding-right: 15px;
  }

  body {
    min-width: 1050px !important;                  
  }

  * {
    outline: none !important;
  }

  .discussion-timeline {
    width: 85%;
  }

  .avatar {
    height: auto !important;
  }

  .UnderlineNav-body {
    width: 100%;
    justify-content: space-evenly;
  }

  .UnderlineNav-item {
    margin-right: 0;
  }
}
