css:
@-moz-document url-prefix("https://www.plurk.com/m") {
/*黑底黃字*/
body {
    background-color: black;
    color: navajowhite;
}
.nick {
    text-shadow: 1px 1px 1px #FFF, 0 0 5px #FFF;
}
  
/*文字輸入框*/
.plurkbox_div {
    background: #d87836;
    color: white;
}
.plurkbox_div form.plurkbox {
    color: white;
}
  
/*超連結顏色變淺*/
a {
    color: #9BAED2;
}
.contents a.meta {
    color: #9BAED2;
}
  
/*廣告變淡*/
.native-ads {
    opacity: 0.5;
}
}
