css:
@-moz-document domain("app.asana.com") {
#asana_ui, .SortableList-itemContainer--row, .asanaView-body, .inboxThreadView-archiveParent, .inbox-archive-parent
        {
            flex-direction: row-reverse !important;
        }

.BoardCard-name, .BoardBody-columnInBoardWithViewMenu .BoardColumn-header, .Board--hasViewMenu .BoardColumn-header, .SingleTaskPane-description, .TaskCommentComposer-composer, .SubtaskTaskRow-taskName, .BlockStory, .SingleTaskTitleRow-taskName .autogrowTextarea-input, .SingleTaskTitleRow-taskName .autogrowTextarea-shadow, .TaskName, .CommentComposer, .BoardCardWithCustomProperties-name, .inboxThreadView-threadGroup, .SidebarCollapsibleHeader
        {
            direction: rtl !important;
        }

        .BoardCard-completionCheckbox, .BoardColumnHeader-dropdownButton, .BlockStory-icon, .BlockStory-mobileIcon, .BoardCardWithCustomProperties-completionCheckbox
        {
            margin-right: 0px !important;
            margin-left: 5px !important;
        }
.blockStoryView-avatar, .SidebarCollapsibleHeader
        {
            margin-right: 0px !important;
            margin-left: 10px !important;
        }
        #right_pane_container
        {
            margin-left: 0px !important;
            margin-right: 15px !important;
        }
        .blockStoryView-metadata, .inboxThreadView-type
        {
            margin-left: 0px !important;
            margin-right: 5px !important;
        }

        .BlockStory-metadata
        {
            margin-right: 10px !important;
        }
        .SidebarItemRow-name
        {
            margin-right: 0px !important;
            margin-left: auto !important;
        }

        .TaskRow-parentTaskName
        {
            flex-grow: initial !important;
        }
        .inboxThreadView
{
    padding-left: 0px !important;
    padding-right: 30px !important;
}
}
