    background: #1a1a1a !important;
}

.panel-body a {
    color: white;
}

.navbar .navbar-brand {
    background: #2a2a2a !important;
}

.navbar .navbar-brand:before {
 	content: 'Zooqle';
    color: white;
    font-size: 1.5em;
}

i.zqf {
    color: white;
}

.cell:hover {
    background: #acacac;
}

.text-muted3 {
    color: #ccc !important;
}

button[title="Search"] i.zqf {
