css:
@-moz-document domain("wink.messengergeek.com") {
img.avatar {
    border-radius: 5%
}
.d-header .icons .icon {
    color: black;
    margin-left: 0px;
    margin-top: -4px;
}
.nav-pills>li.active>a, .nav-pills>li>a.active {
    color: #000000;
    background-color: #ffffff00;
}
.list-controls .category-breadcrumb li.bullet>.badge-category {
    background: #ffffff !important;
    color: #08c !important;
}
.btn {
    border: none;
    color: #ff5353;
    font-weight: normal;
    background: #ffffff;
}
html {
    font-family: Segoe UI;
  }
 .d-header
  {
    background-image: url(https://i.imgur.com/7GBVyEX.png)
}
.d-header #site-logo {
    content: url(https://i.imgur.com/K3CZ5Ks.png);
    max-height: 40px;
    margin-top: -4px
}
img.avatar {
    border-radius: 15%;
    width: 14px;
    height: 14px;
    top: -2px;
    left: -2px;
    border: double orange 0.4em;
    border-image: url("https://i.imgur.com/xWhREdg.png") 4 round stretch;
    
}
.d-header .icons [class^="fa fa-"] {
    font-size: 1.450em;
}
.alert.alert-info.clickable {
    color: #7d7d7d;
}
.alert.alert-info {
    background-color: #d2ffd0;
    color: #000;
}
.topic-list .posters a:first-child .avatar.latest:not(.single) {
    box-shadow: 0 0 0px 0px #a7d5ec;
    border: 0px solid #85c2e0;
    position: relative;
    top: 0px;
    left: 0px;
}
.badge-wrapper.bullet .badge-category-parent-bg, .badge-wrapper.bullet .badge-category-bg {
        border-radius: 50%;
        color: #ff4444;
}
.badge-notification.new-posts, .badge-notification.unread-posts {
    background-color: #f44;
    color: #fff;
    }
.nav-pills, .nav-stacked {
    margin-left: 180px;
    margin-top: -71px;
    position: absolute;
    z-index: 1000;
}
.fa-search:before {
    content: url(https://i.imgur.com/wftpdKQ.png);  

}
.topic-list .posters a:first-child .avatar.latest:not(.single) {
    border: double orange 0.4em;
    border-image: url("https://i.imgur.com/xWhREdg.png") 4 round stretch;
}
}
