css:
@-moz-document domain("docs.google.com") {
/* Comments section */
.docos-enable-docs-header .docos-stream-view {
    position: relative;
    overflow: scroll;
    max-height: 1100px;
}
.docs-docos-activitybox.docos-enable-new-header {
    padding: 0!important;
    height: auto!important;
    width: 600px;
}

/* Comments close to page */
.docos-layout-anchored .docos-anchoreddocoview {
    position: absolute;
    z-index: 500;
    width: 400px;
}
}
