body, #bodyContent, .smwtable {
    background: #0B0B0B !important;
}

.new-section, .pBody, .mw-geshi, code, .i18n, .catlinks, #footer, h1, td, table {
    background: #242424 !important;
}

#p-cactions > div {
    background: #000 !important;
}

