css:
@-moz-document domain("vumoo.to") {
:root{
  --background-color:#111;
  --accent-color:#444;
  --text-color:#fff;
}

body{
  background-image:none!important;
  background-color:var(--background-color)!important;
}

.film-box{
  background-color:transparent!important;
  color:var(--text-color)!important;
}

.film-box h1{
  background-color:transparent!important;
  color:var(--text-color)!important;
}

.intro h3.text-overlay{
  color:var(--text-color)!important;
  background-color:rgba(0, 0, 0, 0.5)!important;
}

.navbar-custom .navbar-header #search-box-wrapper .inner-addon #search-box{
  background-color:var(--accent-color)!important;
  color:var(--text-color)
}

.cinema-dashboard ul.nav-tabs li.active a{
  background-color:transparent!important;
  color:var(--text-color)!important;
}

.cinema-dashboard .tab-content ul.episodes>li>a{
  color:var(--text-color)!important;
}

.hr-divider .hr-divider-heading{
 background-color:var(--background-color)!important;
 color:var(--text-color)!important;
}

.footer ul.keywords li h2 strong, .jumbotron .aloud-bar h1 strong, ul.tag-widget li .tag{
  display:none!important;
}

.footer ul.keywords{
  display:none!important;
}
}
