css:
@-moz-document domain("sketchucation.com") {
.download-plugin, div.inline-attachment dl.file dt, div.inline-attachment dl.file dd, dl.attachbox dd, dl.attachbox>dt, dl.attachbox dl.file, body#phpbb dl.thumbnail>dt>a>img, body#phpbb dl.thumbnail>a>img, body#phpbb dl.thumbnail>a.postlink{
    display: initial !important;
}
dl.attachbox {
	border: 1px dashed ; /*#d8d8d8*/
    border-color: #C9D2D8;
    background-color: #FFFFFF;
}

div.inline-attachment:last-of-type dl.file:before, dl.attachbox:before, body#phpbb dl.thumbnail:first-of-type>a:after {
    display: none !important;
}

}
