css:
@-moz-document domain("servers.minetest.net") {

html {
    color: #888;
}

 body
  {
    background: #383C4A;
    color: #ccc;
  }


#server_list thead {
	background: #2f343f;
}

#server_list tbody tr:nth-child(even) {
	background-color: #545864;
}

#server_list th {
	border-color: #404552;
}


#server_list td {
	border: 1px solid #2b2e39;
}


.mts_hover_list {
	background-color: #2f343f;
	border-color: #5294e2;
}

a:link {
	color: #5294e2;
}

a:visited {
	color: #4c7db6;
}

}
