css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("cpc.to") {
img[src*="images/icons/pi_"],
img[src*="images/icons/icon1.gif"],
img[src*="images/misc/poll_posticon.gif"],
img[src*="images/icons/udaman.gif"],
img[src*="images/icons/repost.gif"] {
        opacity: 0.8 !important;
        width: 16px !important;
        height: 16px !important;
}

div[id*=post_message] fieldset.fieldset {
  border: none !important;
  margin: 0em !important;
  padding: 0em !important;
  margin-top: -4em !important;
}

div[id*=post_message] fieldset.fieldset legend {
  visibility: hidden !important;
}

div[id*=post_message] img[src~="http://static.cpc.to/images/buttons/viewpost.gif"] {
        visibility: hidden !important;
}

div[id*=post_message] fieldset.fieldset > table {
  background-color: #357CD3 !important;
  border-left: 5px solid #3070BE!important;
}

div[id*=post_message] fieldset.fieldset > table div[style*=font]{
  padding-top: 5px !important;
}

table.tdtv {
  background-color: #4E91E4 !important;
}

table.tdtv > tbody > tr > td {
  color: #FFFFFF !important;
}

}
