css:
@-moz-document url-prefix("https://course-evaluation-reports.fas.harvard.edu") {
* {
    font-family: 'Open Sans Semibold', Helvetica, Arial, sans-serif !important;
}
body.fas {
    background: #ECECD8 !important;
    font-size: 16px;
}
body.fas #title {
    background: url(https://a.pomf.cat/wwzake.jpg) 5px 8px no-repeat;
}
body.fas #header {
    background: #600 !important;
    border-bottom: 0px;
}
.course-block {
    background: #FFFFFF;
    border: 0px;
}
.course-block-head {
    background: #F9F9F9;
    border: 0px;
}
h1 {
    background-color: #D06261 !important;
    border-bottom: 0px;
    color: #FFFFFF !important;
    display: inline-block;
    padding: .25rem 1.25rem;
}
h1:hover {
    color: #D06261 !important;
}
h2.term-name {
    display: none;
}
ul.questionPicker li {
    font-variant: none !important;
}
ul.courseSelect {
    background: #FFFFFF;
}
.helpLink {
    background: #FFFFFF !important;
    padding: 10px 8px 0px 0px;
}
.helpLink a {
    background: #FFFFFF !important;
    border: 0px !important;
    font-variant: none;
    font-weight: normal;
    padding: 0px !important;
}

/* Comment this if you need the course planner and print buttons */
.CoursePlanner,
.print {
    display: none !important;
}
#page-content {
    background: #FFFFFF !important;
    border-left: 0px !important;
    border-right: 0px !important;
}
#reportContent {
    background: #FFFFFF !important;
    border-bottom: 0px;
    border-left: 0px;
    border-right: 0px;
}
#subHeader {
    background: #d7e2ec;
    border: 0px;
    clear: left;
    padding: 5px 0px 5px 10px;
}
#summaryStats {
    font-size: 14px;
}
#tabNav {
    margin-top: 50px;
}
#tabNav li {
    background: none;
    border: 0px;
    display: block;
    float: left;
    margin: 0px 3px;
    text-align: center;
}
::-moz-selection {
    background-color: #D06261;
    color: #FFFFFF;
}
::selection {
    background-color: #D06261;
    color: #FFFFFF;
}
}
