css:
@-moz-document domain("archillect.com") {
html,
body {
    background-color: #191919;
    color: #fafafa;
}
.overlay,
#disqus_thread {
    display: none;
}
#loop a svg circle {
    stroke: white;
}
/* Compatibility with my extension https://github.com/Frizbunny/archillect-nav-extension */
#loop a svg path {
    fill: white;    
}
.item {
    opacity: .7;
    transition: opacity 0.1s ease-out;
}
.item:hover {
    opacity: 1;
}
}
