css:
@-moz-document domain(example.com) {
	/* your code goes here */

body {
    font-size: 14px;
    color: #FFF;
    line-height: 18px;
    background: #121212;
    font-family: 'Open Sans', sans-serif;
}
table {
    max-width: 100% !important;
    background-color: transparent;
    border-collapse: collapse;
    border-spacing: 0;
}
.post_content table tbody tr td a {
    font-size: 13px;
    color: #fff;
}
.section {
    background: #141414;
    margin: 0 0 30px;
}
.box1 {
    display: none;
    border-width: 0 1px 1px;
    background: #0000;
    padding: 0 5px;
}
.grybox_single span, .grybox strong {
    color: #b90000;
    color: #fff;
}
.grybox span, .grybox strong {
    color: #b90000;
    color: #fff;
}
.tabs1 li {
    display: inline-block;
    padding: 0 13px 1px;
    color: #fff;
    cursor: pointer;
    background: #0000;
    border: 1px solid #E4E4E4;
        border-bottom-width: 1px;
        border-bottom-style: solid;
        border-bottom-color: rgb(228, 228, 228);
    border-bottom: 1px solid #000;
    position: relative;
}
.tabs1 li.current {
    color: #fff;
    background: #0000;
    padding: 0 13px 2px;
    border: 1px solid #9D9D9D;
        border-bottom-width: 1px;
        border-bottom-style: solid;
        border-bottom-color: rgb(157, 157, 157);
    border-bottom: 1px solid #9D9D9D;
}
.grybox_single, .grybox {
    border: 0px solid #DDD;
}
elemento {
    float: right !important;
    border: 1px solid #DDD0;
}
.grybox {
    background: #0000;
    padding: 1px;
    font-size: 12px;
    margin: 0px 0px 30px !important;
    width: 300px !important;
}
.footer {
    color: #FFFFFF;
    font-size: 11px;
    padding: 10px 0px;
    background: #0e0d0d;
    line-height: 30px;
}
}
