css:
@-moz-document domain('neuralnetworksanddeeplearning.com')
{
p,
body {
    background-color: #eee;
    color: #333;
}

a,
.equation_link {
    color: #6475d6;
}

a:hover,
equation_link:hover {
    color: #254fb2;
}

.nonumber_header,
.header {
    background-color: #6475d6;
}

.MJXp-display,
.MathJax_Display {
    background-color: #ddd;
    border: 1px solid #ccc;
}

h2 {
    font: bold 34px Georgia, serif;
}

.chapter_title {
    font: bold 34px Georgia, serif;
    text-align: center;
}

.chapter_number {
    font: italic 24px Georgia, serif;
}

.section {
    margin: 0 auto;
    width: 55vw;
    max-width: 800px;
    text-align: justify;
}

.h3 {
    text-align: left;
}

.sidebar_title {
    font: italic 16pt Georgia, serif;
    color: #999;
}

p.sidebar {
    color: #aaa;
}

/* Legacy code */

.marginnote {
    right: 3vw;
    left: auto;
    max-width: 15vw;
}

#toc {
    left: 3vw;
    max-width: 15vw;
    text-align: left;
}
}
