css:
@-moz-document url-prefix("https://laravel.com/docs/")
{
  :root
  {
    --fg: #f0f0f0;
    --bg: #222;
    --border: #525252;
    --well: #303030;
  }

  body
  {
    color: var(--fg);
    background-color: var(--bg);
  }

  nav.main,
  nav.main a.brand,
  nav.main ul.main-nav,
  nav.main .switcher,
  .sidebar
  {
    border-color: var(--border);
  }

  nav.main ul.main-nav > li > a
  {
    color: var(--fg);
  }

  .docs article h2 a,
  .docs article h2 a:hover
  {
    color: var(--fg);
  }

  .docs article h1 + ul
  {
    border-color: var(--border);
  }

  .docs article h2 a,
  nav.main ul.main-nav > li > a,
  *
  {
    color: white;
  }

  .docs blockquote
  {
    opacity: 0.95;
  }

  .docs blockquote.has-icon.tip
  {
    background-color: #3683C0;
  }

  :not(pre) > code[class*="language-"],
  code[class^=" language"],
  pre[class^=" language"],
  .browser-window .window-content pre[class*="language-"]
  {
    background: #263238;
    color: #C3D3DE;
  }


  /* Laravel */

  .token.keyword
  {
    color: #C792EA;
  }

  .token.package
  {
    color: #C3D3DE;
  }
  
  .token.delimiter
  { 
    color: #C792EA;
  }

  .token.class-name
  { 
    color: #F78C6C;
  }
  
  .token.function
  {
    color: #82AAFF;
  }

  .token.variable
  {
    color: #EEFFE3;
  }

  .token.punctuation
  {
    color: #89DDFF;
  }

  .token.operator
  {
    color: #89DDFF;
  }

  .token.scope
  {
    color: #FFCB6B;
  }

  .token.string
  {
    color: #C3E88D;
  }

  .token.comment
  {
    color: #546E7A;
  }

  .token.number
  {
    color: #F78C6C;
  }

  .token.property
  {
    color: #EEFFFF;
  }

  .token.constant
  { 
    color: #F78C6C;
  }

  /* Blade */

  .token.tag
  {
    color: #F07171;
  }

  .token.attr-name
  {
    color: #FFCB6B;
  }

  .token.attr-value
  {
    color: #C3E88D;
  }






  code[class*="language-"], pre[class*="language-"]
  {
    text-shadow: none;
  }

  footer.main
  {
    background-color: var(--well);
  }

  footer.main > ul > li > a
  {
    color: var(--fg);
  }
}
